import{_ as p,o as a,c as e,a as r}from"./app.4524d535.js";const h={},t=r('<h1 id="\u300A\u52A8\u7269\u5E84\u56ED\u300B\u8BFB\u4E66\u7B14\u8BB0" tabindex="-1"><a class="header-anchor" href="#\u300A\u52A8\u7269\u5E84\u56ED\u300B\u8BFB\u4E66\u7B14\u8BB0" aria-hidden="true">#</a> \u300A\u52A8\u7269\u5E84\u56ED\u300B\u8BFB\u4E66\u7B14\u8BB0</h1><h2 id="\u5173\u4E8E\u672C\u4E66" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E\u672C\u4E66" aria-hidden="true">#</a> \u5173\u4E8E\u672C\u4E66</h2><blockquote><p>\u4E66\u540D\uFF1A\u52A8\u7269\u5E84\u56ED</p><p>\u4F5C\u8005\uFF1A\u4E54\u6CBB\xB7\u5965\u5A01\u5C14(\u82F1)</p><p>\u81EA\u6211\u8BC4\u5206\uFF1A\u2605\u2605\u2605\u2605\u2605</p><p>\u9605\u8BFB\u65F6\u95F4\uFF1A2020\u5E749\u6708</p></blockquote><h2 id="\u5185\u5BB9\u7B80\u4ECB" tabindex="-1"><a class="header-anchor" href="#\u5185\u5BB9\u7B80\u4ECB" aria-hidden="true">#</a> \u5185\u5BB9\u7B80\u4ECB</h2><p>\u519C\u573A\u7684\u4E00\u7FA4\u52A8\u7269\u6210\u529F\u5730\u8FDB\u884C\u4E86\u4E00\u573A\u201C\u9769\u547D\u201D\uFF0C\u5C06\u538B\u69A8\u4ED6\u4EEC\u7684\u4EBA\u7C7B\u4E1C\u5BB6\u8D76\u51FA\u519C\u573A\uFF0C\u5EFA\u7ACB\u8D77\u4E00\u4E2A\u5E73\u7B49\u7684\u52A8\u7269\u793E\u4F1A\u3002\u7136\u800C\uFF0C\u52A8\u7269\u9886\u8896\uFF0C\u90A3\u4E9B\u806A\u660E\u7684\u732A\u4EEC\u6700\u7EC8\u5374\u7BE1\u593A\u4E86\u9769\u547D\u7684\u679C\u5B9E\uFF0C\u6210\u4E3A\u6BD4\u4EBA\u7C7B\u4E1C\u5BB6\u66F4\u52A0\u72EC\u88C1\u548C\u6781\u6743\u7684\u7EDF\u6CBB\u8005\u3002</p><h2 id="\u4EBA\u7269" tabindex="-1"><a class="header-anchor" href="#\u4EBA\u7269" aria-hidden="true">#</a> \u4EBA\u7269</h2><p>\u743C\u65AF\u2014\u2014\u519C\u573A\u4E3B</p><p>\u62FF\u7834\u4ED1(\u732A)\u2014\u2014\u81EA\u79C1\u81EA\u5229\u3001\u64C5\u6743\u672F\u3001\u61C2\u653F\u6CBB</p><p>\u65AF\u8BFA\u9C8D(\u732A)\u2014\u2014\u6709\u7406\u60F3\uFF0C\u771F\u6B63\u7684\u4E3A\u4EBA\u6C11\u670D\u52A1</p><p>\u65AF\u594E\u62C9(\u732A)\u2014\u2014\u62FF\u7834\u4ED1\u8D70\u72D7\uFF0C\u63A9\u76D6\u771F\u76F8\u3001\u7C89\u9970\u592A\u5E73</p><p>\u9C8D\u514B\u745F(\u9A6C)\u2014\u2014\u6700\u5FE0\u8BDA\u7684\u4FE1\u5F92</p><p>\u83AB\u8389(\u9A6C)\u2014\u2014\u865A\u8363\u3001\u6015\u5403\u82E6\uFF0C\u80CC\u53DB\u9769\u547D</p><p>\u672C\u6770\u660E(\u9A74)\u2014\u2014\u667A\u8005\uFF0C\u660E\u54F2\u4FDD\u8EAB</p><p>\u72D7\u2014\u2014\u62FF\u7834\u4ED1\u7684\u6253\u624B\uFF0C\u653F\u6743\u5F97\u4EE5\u7A33\u5B9A\u7684\u652F\u67F1\uFF0C\u5F71\u5C04\u519B\u961F</p><p>\u732B\u2014\u2014\u65E0\u4FE1\u4EF0\uFF0C\u968F\u5927\u6D41\uFF0C\u7231\u5077\u61D2</p><p>\u7F8A\u2014\u2014\u6CA1\u6587\u5316\uFF0C\u76F2\u76EE\u65E0\u77E5</p><p>\u9E21\u3001\u9E2D\u3001\u9E3D\u5B50\u3001\u725B\u3001\u7F8A\u2014\u2014\u52B3\u82E6\u5927\u4F17</p><h2 id="\u4E03\u8BEB" tabindex="-1"><a class="header-anchor" href="#\u4E03\u8BEB" aria-hidden="true">#</a> \u4E03\u8BEB</h2><p>\u52A8\u7269\u5E84\u56ED\u7684\u7B2C\u4E00\u90E8\u5BAA\u6CD5\u201C\u4E03\u8BEB\u201D\u4EE5\u53CA\u540E\u6765\u88AB\u62FF\u7834\u4ED1\u4FEE\u6539\u524D\u540E\u5BF9\u6BD4\uFF1A</p><p>1\uFF0E\u51E1\u7528\u4E24\u6761\u817F\u8D70\u8DEF\u7684\u90FD\u662F\u654C\u4EBA\uFF1B</p><p>2\uFF0E\u51E1\u7528\u56DB\u6761\u817F\u8D70\u8DEF\u6216\u8005\u6709\u7FC5\u8180\u7684\u90FD\u662F\u670B\u53CB\uFF1B</p><p>3\uFF0E\u4EFB\u4F55\u52A8\u7269\u4E0D\u5F97\u7740\u8863\uFF1B</p><p>4\uFF0E\u4EFB\u4F55\u52A8\u7269\u4E0D\u5F97\u5367\u5E8A\uFF1B \u2014\u2014<strong>\u4EFB\u4F55\u52A8\u7269\u4E0D\u5F97\u5728\u94FA\u88AB\u8925\u7684\u5E8A\u4E0A\u7761\u89C9</strong></p><p>5\uFF0E\u4EFB\u4F55\u52A8\u7269\u4E0D\u5F97\u996E\u9152\uFF1B \u2014\u2014<strong>\u4EFB\u4F55\u52A8\u7269\u4E0D\u5F97\u8FC7\u91CF\u996E\u9152\uFF08\u62FF\u7834\u4ED1\u65E9\u5C31\u5F00\u59CB\u996E\u9152\uFF0C\u5229\u7528\u548C\u6E29\u666E\u5C14\u7684\u4EA4\u6613\u6362\u9152)</strong></p><p>6\uFF0E\u4EFB\u4F55\u52A8\u7269\u4E0D\u5F97\u4F24\u5BB3\u5176\u4ED6\u52A8\u7269\uFF1B\u2014\u2014 <strong>\u4EFB\u4F55\u52A8\u7269\u4E0D\u5F97\u65E0\u7F18\u65E0\u6545\u4F24\u5BB3\u5176\u4ED6\u52A8\u7269</strong></p><p>7\uFF0E\u6240\u6709\u52A8\u7269\u4E00\u5F8B\u5E73\u7B49\u2014\u2014<strong>\u6240\u6709\u52A8\u7269\u4E00\u5F8B\u5E73\u7B49\uFF0C\u4F46\u6709\u4E9B\u52A8\u7269\u6BD4\u5176\u4ED6\u52A8\u7269\u66F4\u5E73\u7B49\u3002</strong></p><h2 id="\u611F\u60F3" tabindex="-1"><a class="header-anchor" href="#\u611F\u60F3" aria-hidden="true">#</a> \u611F\u60F3</h2><p>\u53D7\u5230\u52A8\u7269\u4E3B\u4E49\u601D\u60F3\u800C\u8D77\u4E49\u7684\u52A8\u7269\u4EEC\uFF0C\u9AD8\u5531\u201C\u82F1\u683C\u5170\u517D\u201D\uFF0C\u63A8\u7FFB\u4E86\u519C\u573A\u4E3B\u743C\u65AF\u7684\u7EDF\u6CBB\uFF0C\u7D27\u63A5\u7740\u4EBA\u7C7B\u53CD\u653B\uFF0C\u7206\u53D1\u725B\u68DA\u5927\u6218\uFF0C\u8FD9\u573A\u6218\u6597\u727A\u7272\u8005\u4E0D\u5C11\uFF0C\u8270\u96BE\u7684\u7559\u4F4F\u4E86\u9769\u547D\u7684\u679C\u5B9E\u3002</p><p>\u5F53\u5916\u754C\u73AF\u5883\u8D8B\u4E8E\u7A33\u5B9A\uFF0C\u5185\u90E8\u5C31\u4F1A\u6709\u5206\u6B67\uFF0C\u65AF\u8BFA\u9C8D\u60F3\u641E\u5EFA\u8BBE\uFF0C\u5E0C\u671B\u5EFA\u9020\u5DE5\u7A0B\u548C\u5174\u529E\u6559\u80B2\u4EE5\u63D0\u9AD8\u751F\u4EA7\u529B\uFF0C\u8BA9\u4EBA\u4EEC\u8FC7\u4E0A\u4E00\u5468\u5DE5\u4F5C\u4E09\u5929\u7684\u5B89\u9038\u751F\u6D3B\uFF0C\u4E0D\u518D\u6709\u5265\u524A\u548C\u538B\u8FEB\u3002\u800C\u62FF\u7834\u4ED1\u53EF\u4E0D\u8FD9\u4E48\u60F3\uFF0C\u65AF\u8BFA\u9C8D\u4F5C\u4E3A\u653F\u654C\u53D7\u5230\u4EBA\u4EEC\u7684\u7231\u6234\uFF0C\u90A3\u5C31\u662F\u5BF9\u81EA\u5DF1\u653F\u6CBB\u5730\u4F4D\u7684\u64BC\u52A8\uFF0C\u5FC5\u987B\u9664\u6389\u653F\u654C\u624D\u80FD\u5B9E\u73B0\u6309\u81EA\u5DF1\u610F\u613F\u7684\u7EDF\u6CBB\u3002</p><p>\u65AF\u8BFA\u9C8D\u663E\u7136\u662F\u4E0D\u61C2\u653F\u6CBB\u7684\uFF0C\u653F\u6CBB\u5C31\u662F\u8981\u57F9\u517B\u4E00\u6279\u652F\u6301\u81EA\u5DF1\u7684\u52BF\u529B\uFF0C\u4E0D\u7136\u65E0\u6CD5\u7ACB\u8DB3\u3002\u62FF\u7834\u4ED1\u5F00\u59CB\u5077\u5077\u517B\u4E869\u6761\u72D7\uFF0C\u7B49\u518D\u6B21\u53D1\u751F\u653F\u6CBB\u535A\u5F08\u7684\u65F6\u5019\uFF0C\u4EE5\u6B64\u503E\u8F67\u653F\u654C\uFF0C\u8FEB\u5BB3\u8BD5\u56FE\u53CD\u6297\u81EA\u5DF1\u7684\u52A8\u7269\u3002\u663E\u7136\u8FD9\u5C31\u80FD\u731C\u5230\u540E\u6765\u7684\u60B2\u5267\uFF0C\u4E00\u4E9B\u8BD5\u56FE\u53CD\u6297\u7684\u52A8\u7269\u88AB\u6263\u4E0A\u53CD\u52A8\u7684\u5E3D\u5B50\uFF0C\u5728\u6240\u8C13\u7684\u52A8\u7269\u4E3B\u4E49\u601D\u60F3\u4E0B\uFF0C\u906D\u5230\u4E25\u9177\u7684\u8FEB\u5BB3\uFF0C\u6B7B\u540E\u8FD8\u7559\u4E0B\u9A82\u540D\u3002</p><p>\u52A8\u7269\u4EEC\u56DE\u60F3\u8D77\u597D\u50CF\u4E0D\u592A\u5BF9\u52B2\uFF0C\u5F00\u59CB\u53BB\u627E\u201C\u4E03\u8BEB\u201D\uFF0C\u6CA1\u60F3\u5230\u8FD9\u4E1C\u897F\u65E9\u5C31\u6539\u597D\u4E86\uFF0C\u800C\u52A8\u7269\u4EEC\u5728\u65AF\u594E\u62C9\u7684\u5FFD\u60A0\u4E0B\uFF0C\u6E10\u6E10\u5FD8\u8BB0\u6700\u521D\u7684\u201C\u4E03\u8BEB\u201D\u662F\u4EC0\u4E48\u4E86\uFF0C\u4E0D\u5F97\u4E0D\u9ED8\u9ED8\u7684\u80AF\u5B9A\u73B0\u5728\u7684\u201C\u4E03\u8BEB\u201D\u3002</p><p>\u98CE\u8F66\u88AB\u6BC1\u4E86\u4E24\u6B21\uFF0C\u5176\u4E2D\u9C8D\u514B\u745F\u7684\u5F62\u8C61\u6700\u663E\u8457\uFF0C\u5B83\u4EFB\u52B3\u4EFB\u6028\uFF0C\u53E3\u5934\u7985\uFF1A\u201C\u6211\u8981\u66F4\u52A0\u52AA\u529B\u5DE5\u4F5C\u201D\u3001\u201C\u62FF\u7834\u4ED1\u8BF4\u7684\u90FD\u662F\u5BF9\u7684\u201D\uFF0C\u662F\u6700\u5FE0\u5B9E\u7684\u4FE1\u5F92\uFF0C\u53EF\u7B49\u5230\u5B83\u4E34\u8FD1\u9000\u4F11\u8EAB\u4F53\u652F\u6301\u4E0D\u4F4F\u65F6\uFF0C\u5374\u88AB\u65E0\u60C5\u7684\u9001\u5230\u5C60\u9A6C\u573A\u3002</p><p>\u6700\u540E\uFF0C\u732A\u53EF\u4EE5\u6BEB\u4E0D\u906E\u63A9\u7684\u4E24\u6761\u817F\u8D70\u8DEF\u4E86\uFF0C\u7F8A\u4E5F\u6539\u53E3\uFF0C\u201C\u56DB\u53EA\u817F\u597D\uFF0C\u4E24\u53EA\u817F\u66F4\u597D\u201D\u3002\u665A\u4E0A\u52A8\u7269\u4EEC\u5728\u7A97\u5916\u5077\u770B\u5230\u4EBA\u732A\u5171\u9910\u6253\u724C\uFF0C\u90A3\u79CD\u76F8\u4F3C\u7684\u795E\u6001\u548C\u52A8\u4F5C\uFF0C\u5DF2\u7ECF\u5206\u4E0D\u6E05\u662F\u4EBA\u8FD8\u662F\u732A\u4E86\u3002</p><p>\u603B\u7ED3\u4E0B\u62FF\u7834\u4ED1\u6240\u7528\u7684\u624B\u8155\u5427\uFF1A</p><p>1.\u635F\u574F\u522B\u4EBA\uFF0C\u5305\u88C5\u81EA\u5DF1\u3002\u574F\u4E8B\u90FD\u8F6C\u79FB\u77DB\u76FE\u7ED9\u65AF\u8BFA\u9C8D\uFF0C\u597D\u4E8B\u5168\u662F\u62FF\u7834\u4ED1\u601D\u60F3\u7684\u529F\u52B3</p><p>2.\u57F9\u517B\u653F\u6CBB\u52BF\u529B\uFF0C\u51E1\u662F\u53CD\u6297\u8005\u7686\u4EE5\u66B4\u529B\u9547\u538B\uFF0C\u5E76\u6263\u4E0A\u6076\u540D</p><p>3.\u4E00\u6B65\u6B65\u5F97\u5BF8\u8FDB\u5C3A\uFF0C\u53CD\u590D\u4FEE\u6539\u5BAA\u6CD5\uFF0C\u8BA9\u884C\u4E3A\u5408\u6CD5\u5316</p><p>4.\u641E\u4E2A\u4EBA\u5D07\u62DC\u3001\u641E\u5B64\u7ACB\u4E3B\u4E49\uFF0C\u7EDD\u5BF9\u6743\u5A01\u6CA1\u6709\u4EBA\u6562\u8D28\u7591\uFF0C\u8D28\u7591\u4E5F\u4E0D\u6562\u8BF4\u51FA\u6765</p><p>5.\u611A\u6C11\u653F\u7B56\uFF0C\u4EBA\u6C11\u53EA\u61C2\u5F97\u558A\u53E3\u53F7\uFF0C\u7BE1\u6539\u653F\u6CBB\u6982\u5FF5\uFF0C\u6D88\u62B9\u4EBA\u6C11\u65E9\u671F\u7684\u5386\u53F2\u8BB0\u5FC6</p><p>\u6240\u6709\u52A8\u7269\u4E00\u5F8B\u5E73\u7B49\uFF0C\u4F46\u6709\u7684\u52A8\u7269\u6BD4\u5176\u4ED6\u52A8\u7269\u66F4\u5E73\u7B49\uFF0C\u8FD9\u90E8\u8BBD\u523A\u7684\u5BD3\u8A00\u51C6\u786E\u9884\u8A00\u4E86\u540E\u6765\u7684\u7EA2\u592A\u9633\u65F6\u4EE3\uFF0C\u76F4\u5230\u4ECA\u5929\u4ECD\u80FD\u89C1\u5230\u52A8\u7269\u5E84\u56ED\u7684\u5F71\u5B50\u3002</p>',40),n=[t];function d(o,s){return a(),e("div",null,n)}var c=p(h,[["render",d],["__file","\u300A\u52A8\u7269\u5E84\u56ED\u300B\u8BFB\u4E66\u7B14\u8BB0.html.vue"]]);export{c as default};
