import{_ as p,o as a,c as e,a as r}from"./app.4524d535.js";const h={},d=r('<h1 id="\u300A\u628A\u8BDD\u8BF4\u5230\u5FC3\u7A9D\u5B50\u91CC\u300B\u8BFB\u4E66\u7B14\u8BB0" tabindex="-1"><a class="header-anchor" href="#\u300A\u628A\u8BDD\u8BF4\u5230\u5FC3\u7A9D\u5B50\u91CC\u300B\u8BFB\u4E66\u7B14\u8BB0" aria-hidden="true">#</a> \u300A\u628A\u8BDD\u8BF4\u5230\u5FC3\u7A9D\u5B50\u91CC\u300B\u8BFB\u4E66\u7B14\u8BB0</h1><h2 id="\u5173\u4E8E\u672C\u4E66" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E\u672C\u4E66" aria-hidden="true">#</a> \u5173\u4E8E\u672C\u4E66</h2><blockquote><p>\u4E66\u540D\uFF1A\u628A\u8BDD\u8BF4\u5230\u5FC3\u7A9D\u5B50\u91CC\uFF08\u5168\u4E24\u518C\uFF09</p><p>\u4F5C\u8005\uFF1A\u5218\u5889(\u7F8E)</p><p>\u81EA\u6211\u8BC4\u5206\uFF1A\u2605\u2605\u2605</p><p>\u9605\u8BFB\u65F6\u95F4\uFF1A2020\u5E747\u6708</p></blockquote><h2 id="\u5185\u5BB9\u7B80\u4ECB" tabindex="-1"><a class="header-anchor" href="#\u5185\u5BB9\u7B80\u4ECB" aria-hidden="true">#</a> \u5185\u5BB9\u7B80\u4ECB</h2><p>\u9E21\u6C64\u7238\u7238\u5218\u5889\u529B\u4F5C\uFF0C\u5173\u4E8E\u8BF4\u8BDD\u7684\u539F\u5219</p><h2 id="\u6458\u8981" tabindex="-1"><a class="header-anchor" href="#\u6458\u8981" aria-hidden="true">#</a> \u6458\u8981</h2><h3 id="\u4E0A\u518C" tabindex="-1"><a class="header-anchor" href="#\u4E0A\u518C" aria-hidden="true">#</a> \u4E0A\u518C</h3><p>\u4E00\u3001\u201C\u96C4\u8FA9\u662F\u94F6\uFF0C\u6C89\u9ED8\u662F\u91D1\u201D</p><p>\u6C89\u9ED8\u662F\u4E3A\u4E86\u663E\u793A\u6DF1\u85CF\u4E0D\u9732\uFF0C\u540C\u65F6\u900F\u9732\u4E00\u4E9B\u4EE4\u5BF9\u65B9\u56F0\u60D1\u7684\u6D88\u606F\uFF0C\u907F\u514D\u81EA\u5DF1\u9ED4\u9A74\u6280\u7A77\u800C\u66B4\u9732\u3002</p><p>\u4E8C\u3001\u58F0\u4E1C\u51FB\u897F\uFF0C\u6709\u8BDD\u597D\u8BF4</p><p>1.\u6BCF\u4E2A\u4EBA\u90FD\u61C2\u5F97\u201C\u906E\u201D\uFF0C\u4E5F\u61C2\u5F97\u201C\u9690\u6076\u626C\u5584\u201D\uFF0C\u6709\u65F6\u5019\u522B\u4EBA\u6CA1\u8BF4\u534A\u53E5\u8C0E\u8A00\uFF0C\u53EA\u8BF4\u597D\uFF0C\u8BA9\u4F60\u5FD8\u8BB0\u4ED6\u6CA1\u8BF4\u7684\u6076\u3002</p><p>2.\u8A00\u591A\u5FC5\u5931\uFF0C\u4F60\u201C\u8A00\u591A\u201D\u53EF\u80FD\u4F7F\u522B\u4EBA\u5931\u53BB\u89C2\u5BDF\u529B\u751A\u81F3\u521B\u9020\u529B\uFF0C\u6240\u4EE5\u9500\u552E\u4EBA\u5458\u603B\u559C\u6B22\u8BF4\uFF0C\u5C31\u662F\u4E3A\u4E86\u628A\u4F60\u5E26\u504F\u3002\u800C\u81EA\u5DF1\u4E5F\u8981\u8BB0\u5F97\u4E0D\u8981\u8F7B\u6613\u6253\u65AD\u522B\u4EBA\u5DE5\u4F5C\u8FC7\u7A0B\uFF0C\u5982\u8001\u5A46\u6570\u94B1\u3001\u533B\u751F\u8BCA\u65AD\u3001\u5B69\u5B50\u5199\u4F5C\u4E1A\u7B49\u3002</p><p>\u4E09\u3001\u8BF4\u5927\u4EBA\uFF0C\u5219\u85D0\u4E4B</p><p>\u4E0D\u80FD\u8BA9\u5927\u4EBA\u7269\u4E0E\u81EA\u5DF1\u63E1\u624B\u65F6\u611F\u5230\u51B0\u51C9\uFF0C\u4E0D\u7136\u6C14\u52BF\u4E0A\u5C31\u8F93\u4E09\u5206\uFF0C\u8BBE\u6CD5\u5E73\u9759\u81EA\u5DF1\u7684\u5FC3\u5883\u3002</p><p>\u56DB\u3001\u91CD\u8981\u7684\u4E8B\u60C5\u5148\u8BF4\u7ED3\u679C\uFF0C\u540E\u9053\u539F\u59D4</p><p>\u8FD9\u662F\u201C\u65B0\u95FB\u201D\u7684\u505A\u6CD5\uFF0C\u53CD\u8FC7\u6765\u662F\u201C\u5C0F\u8BF4\u201D\u3002</p><p>\u4E94\u3001\u574F\u8BDD\u8981\u7F13\u8BF4</p><p>\u4EE4\u4EBA\u654F\u611F\u7684\u7ED3\u679C\u6700\u597D\u4E0D\u8981\u5355\u5200\u76F4\u5165\uFF0C\u5148\u201C\u8BBE\u5B9A\u5E95\u7EBF\u201D\uFF0C\u4F7F\u5BF9\u65B9\u89C9\u5F97\u7CDF\u4E0D\u80FD\u54EA\u53BB\uFF0C\u540E\u7ECF\u201C\u5BF9\u6BD4\u201D\uFF0C\u4F7F\u539F\u5148\u7ED3\u679C\u4E0D\u90A3\u4E48\u523A\u8033\u3002</p><p>\u516D\u3001\u8F7B\u8BDD\u4E0D\u80FD\u91CD\u8BF4\uFF0C\u574F\u8BDD\u53EF\u4EE5\u597D\u8BF4</p><p>\u4E3A\u4E86\u8BA9\u5B69\u5B50\u5BB6\u957F\u7EE7\u7EED\u82B1\u94B1\u4E0A\u57F9\u8BAD\u73ED\uFF0C\u53EF\u4EE5\u5148\u5938\u5956\u5B69\u5B50\u8FDB\u6B65\u5927\uFF0C\u518D\u8BA9\u5BB6\u957F\u4E3A\u4E86\u8BA9\u5B69\u5B50\u8FBE\u5230\u66F4\u9AD8\u6C34\u5E73\u800C\u82B1\u94B1\u3002</p><p>\u4E03\u3001\u5E73\u65F6\u5C11\u5F00\u53E3\uFF0C\u5076\u7136\u5F00\u53E3\uFF0C\u5F00\u53E3\u6210\u91D1</p><p>1.\u5929\u5929\u89C1\u9762\u7684\u9152\u8089\u670B\u53CB\uFF0C\u62DC\u6258\u7684\u8BDD\u53CD\u800C\u5ACC\u6DE1\uFF0C\u62DC\u6258\u7684\u529B\u91CF\u5DF2\u7ECF\u5F31\u7684\u4E0D\u80FD\u518D\u5F31\u4E86\u3002\u800C\u5E73\u65F6\u5C11\u5F00\u53E3\u7684\u4E00\u5F00\u53E3\uFF0C\u5FC5\u7136\u4F1A\u5F88\u611F\u6FC0\u4F60\u3002</p><p>2.\u76F4\u9500\u5927\u4F1A\uFF0C\u53D1\u5C55\u4E0B\u7EBF\u3002\u81EA\u5DF1\u7ED9\u81EA\u5DF1\u50AC\u7720\uFF0C\u5F53\u81EA\u5DF1\u6DF1\u4FE1\u65F6\uFF0C\u518D\u8BA9\u522B\u4EBA\u76F8\u4FE1\u3002</p><p>3.\u8BDA\u5B9E\u662F\u6700\u4FDD\u9669\u7684\u8BF4\u8BDD\u65B9\u6CD5\uFF0C\u8BDD\u53EF\u4EE5\u865A\uFF0C\u4F46\u5FC5\u987B\u771F\u3002</p><p>\u516B\u3001\u653E\u8BDD\u7684\u827A\u672F</p><p>1.\u201C\u5148\u5C0F\u4EBA\uFF0C\u540E\u541B\u5B50\u201D\uFF0C\u653E\u8BDD\u6700\u597D\u7ECF\u8FC7\u522B\u4EBA\uFF0C\u6BD4\u5982\u79D8\u4E66\u3002\u95F4\u63A5\u653E\u8BDD\u6700\u5927\u7684\u597D\u5904\uFF0C\u4E0D\u6210\u53EF\u4EE5\u4E0D\u8BA4\u8D26\uFF0C\u6210\u4E86\u53EF\u4EE5\u8FDB\u4E00\u6B65\uFF0C\u5E38\u89C1\u5546\u4E1A\u9886\u5BFC\u4EBA\u89C1\u7684\u535A\u5F08\u3002</p><p>2.\u653E\u8BDD\uFF0C\u6700\u91CD\u8981\u7684\u529F\u80FD\u662F\u201C\u5361\u4F4D\u201D\uFF0C\u628A\u5BF9\u65B9\u4F4D\u7F6E\u5148\u5361\u6B7B\uFF0C\u8BA9\u5BF9\u65B9\u7684\u62DB\u6570\u4F7F\u4E0D\u51FA\u6765\u3002</p><p>3.\u4E3B\u5C06\u51B3\u4E0D\u5BF9\u526F\u624B\uFF0C\u4E0D\u80FD\u4EE5\u81EA\u5DF1\u4E3B\u5C06\u5BF9\u654C\u65B9\u526F\u5C06\uFF0C\u66F4\u4E0D\u53EF\u4EB2\u81EA\u51FA\u9A6C\uFF0C\u5426\u5219\uFF0C\u521A\u4EA4\u624B\uFF0C\u56E0\u4E3A\u8EAB\u4EFD\u4E0D\u540C\uFF0C\u4F60\u5DF2\u7ECF\u8F93\u5728\u201C\u4F60\u6CA1\u6709\u4EBA\u201D\u4EE5\u53CA\u4F60\u6BDB\u8E81\u4E0A\u3002</p><p>\u4E5D\u3001\u51E0\u6761\u539F\u5219</p><p>1.\u65E0\u6CD5\u6539\u53D8\u4E8B\u5B9E\u7684\u8BDD\u4E0D\u8BF4\u3002\u5373\u5BF9\u8FC7\u53BB\u6216\u73B0\u5728\u7684\u865A\u62DF\u3001\u672A\u6765\u4E0D\u53EF\u80FD\u6539\u53D8\u3002\u6BD4\u5982\u76AE\u80A4\u9ED1\u767D\uFF0C\u5934\u53D1\u767D\u4E86\u3001\u79C3\u5B50\u3001\u9AD8\u77EE\u7B49\u3002\u76F8\u53CD\uFF0C\u6709\u4E9B\u4E1C\u897F\u770B\u4F3C\u4E0D\u80FD\u6539\u53D8\uFF0C\u5374\u80FD\u8C08\u3002\u5982\u80D6\u53EF\u4EE5\u901A\u8FC7\u4F60\u7ED9\u7684\u65B9\u6CD5\u53D8\u7626\uFF0C\u5C06\u4FDD\u5065\u54C1\u5356\u7ED9\u8001\u4EBA\u7684\u9A97\u672F\u3002</p><p>2.\u4F1A\u9020\u6210\u5206\u5316\u6548\u679C\u7684\u8BDD\u4E0D\u8BF4\u3002\u7236\u6BCD\u5728\u5916\u4EBA\u9762\u524D\u8D5E\u7F8E\u81EA\u5DF1\u5B69\u5B50\u4E2D\u7684\u4E00\u4E2A\uFF0C\u8D5E\u7F8E\u4E86\u7532\u65F6\u5019\u540C\u65F6\u4F24\u5BB3\u4E86\u4E59\u3002</p><p>3.\u4F1A\u4F24\u4EBA\u81EA\u5C0A\u7684\u8BDD\u4E0D\u80FD\u8BF4\u3002\u4EBA\u5F80\u5F80\u628A\u9519\u8BEF\u5F52\u56E0\u5230\u80FD\u529B\u4E0D\u8DB3\uFF0C\u800C\u4E0D\u662F\u5929\u8D4B\u4E0A\uFF0C\u6B64\u65F6\u4E0D\u80FD\u70B9\u7834\u3002</p><p>\u5341\u3001\u5DF2\u7ECF\u5982\u6B64\uFF0C\u4F55\u5FC5\u591A\u8BF4\uFF1F</p><p>\u505A\u5230\u8FD9\u6761\u9700\u8981\u5927\u5FCD\u8010\u3001\u597D\u4FEE\u517B\uFF0C\u6C89\u6CA1\u6389\u7684\u6210\u672C\u518D\u600E\u4E48\u6028\u5929\u5C24\u4EBA\u4E5F\u6CA1\u6709\u7528\u3002\u65E2\u4E0A\u8D3C\u8239\uFF0C\u4F55\u5FC5\u54C0\u568E\uFF1F</p><p>\u5341\u4E00\u3001\u4EE5\u8FDB\u4E3A\u9000\uFF0C\u4EE5\u9000\u4E3A\u8FDB</p><p>1.\u7ED3\u679C\u5DF2\u6210\u5B9A\u5C40\uFF0C\u4E3B\u52A8\u51FA\u51FB\uFF0C\u53CD\u800C\u6709\u53EF\u80FD\u53CD\u8F6C\u3002\u5982\u77E5\u9053\u522B\u4EBA\u6765\u501F\u94B1\uFF0C\u81EA\u5DF1\u4E3B\u52A8\u8868\u793A\u5411\u4ED6\u501F\u94B1\u7684\u610F\u601D\uFF0C\u628A\u8BDD\u5835\u56DE\u53BB\u3002</p><p>2.\u9762\u5BF9\u6279\u8BC4\uFF0C\u5148\u505A\u80AF\u5B9A\uFF0C\u8868\u793A\u81EA\u5DF1\u8FD8\u6B20\u706B\u5019\uFF0C\u4EE5\u9000\u4E3A\u8FDB\u3002</p><p>3.\u5148\u201C\u4E3B\u63A7\u201D\u518D\u201C\u9000\u8DEF\u201D\uFF0C\u4E3B\u52A8\u4EE5\u5FD9\u788C\u7B49\u539F\u56E0\u627F\u62C5\u66F4\u5C0F\u7684\u8D23\u4EFB\uFF0C\u907F\u514D\u88AB\u4F17\u4EBA\u63A8\u4E3E\u4E0A\u53BB\u8D1F\u5927\u8D23\u4EFB\u3002</p><p>4.\u5728\u7ADE\u4E89\u6FC0\u70C8\u573A\u5408\uFF0C\u603B\u6709\u4EBA\u53D7\u5230\u6279\u8BC4\uFF0C\u603B\u662F\u843D\u5230\u7F3A\u5E2D\u8005\u7684\u5934\u4E0A\uFF0C\u8C01\u5F31\u6B3A\u8D1F\u8C01\uFF0C\u8981\u8868\u660E\u81EA\u5DF1\u7EA0\u7F20\u5230\u5E95\uFF0C\u4E0D\u662F\u597D\u6B3A\u8D1F\u7684\u3002</p><h3 id="\u4E0B\u518C" tabindex="-1"><a class="header-anchor" href="#\u4E0B\u518C" aria-hidden="true">#</a> \u4E0B\u518C</h3><p>\u4E00\u3001\u201C\u6CA1\u6709\u529F\u52B3\uFF0C\u4E5F\u6709\u82E6\u52B3\u201D\uFF0C\u201C\u65BD\u4EBA\u614E\u52FF\u8A00\uFF0C\u53D7\u65BD\u5F53\u8A00\u62A5\u201D</p><p>1.\u4EBA\u90FD\u6709\u8FD9\u4E2A\u5FC3\u7406\uFF0C\u5C31\u662F\u4E0D\u613F\u610F\u9886\u4F60\u7684\u60C5\uFF0C\u56E0\u4E3A\u6B20\u94B1\u8FD8\u80FD\u8FD8\uFF0C\u94B1\u6709\u4E00\u5B9A\u6570\u76EE\uFF0C\u60C5\u5374\u662F\u62BD\u8C61\u7684\uFF0C\u6B20\u4E86\uFF0C\u4E0D\u77E5\u600E\u4E48\u8FD8\u3002\u5E2E\u52A9\u522B\u4EBA\uFF0C\u4ED6\u5FC3\u91CC\u77E5\u9053\uFF0C\u4E0D\u5FC5\u4F60\u53BB\u9080\u529F\uFF0C\u8BA9\u4ED6\u63A5\u4F60\u7684\u60C5\u3002</p><p>2.\u5F53\u522B\u4EBA\u9080\u529F\uFF0C\u4F60\u542C\u4E86\u4E0D\u9AD8\u5174\uFF0C\u4ED6\u4E0D\u9080\u529F\uFF0C\u53C8\u751F\u6015\u522B\u4EBA\u4E0D\u77E5\u9053\u3001\u4E0D\u9886\u60C5\uFF0C\u8FD9\u65F6\u4F60\u8981\u4E3B\u52A8\u8BF4\u3002</p><p>\u4E8C\u3001\u5C0F\u5FC3\u9694\u5899\u6709\u8033</p><p>1.\u5148\u63A2\u865A\u5B9E\uFF0C\u5224\u65AD\u5BF9\u65B9\u662F\u5426\u6709\u65F6\u95F4\u3001\u662F\u5426\u6B64\u65F6\u65B9\u4FBF\u8BF4\u8BDD\uFF0C\u5982\u679C\u81EA\u5DF1\u4E0D\u65B9\u4FBF\u8BF4\u67D0\u4E9B\u8BDD\uFF0C\u5F97\u8BA9\u5BF9\u65B9\u77E5\u6653\u4F60\u6240\u5904\u7684\u73AF\u5883\u3002</p><p>2.\u5728\u5B89\u9759\u7684\u5730\u65B9\u5E94\u628A\u8BDD\u7B52\u8D34\u8FD1\u8033\u6735\u514D\u5F97\u8033\u5C16\u7684\u4EBA\u542C\u5230\uFF0C\u66F4\u4E0D\u80FD\u5728\u8BFB\u5230\u201C\u90A3\u4EBA\u201D\u65F6\uFF0C\u7528\u95EA\u70C1\u7684\u773C\u795E\u5077\u770B\u90A3\u4E2A\u4EBA\u3002\u4E00\u4E2A\u4F1A\u8BF4\u8BDD\u7684\u4EBA\uFF0C\u603B\u80FD\u63A2\u77E5\u5BF9\u65B9\u7684\u60F3\u6CD5\uFF1B\u4E00\u4E2A\u4F1A\u6253\u7535\u8BDD\u7684\u4EBA\uFF0C\u603B\u8981\u68C0\u6D4B\u5BF9\u65B9\u5F53\u65F6\u7684\u73AF\u5883\uFF0C\u9002\u5F53\u7684\u73AF\u5883\u624D\u80FD\u628A\u8BDD\u8BF4\u5230\u5FC3\u7A9D\u5B50\u91CC\u3002</p><p>\u4E09\u3001\u5F15\u7528\u522B\u4EBA\u7684\u8BDD</p><p>\u501F\u7528\u522B\u4EBA\u7684\u8BDD\uFF0C\u4E5F\u662F\u627E\u501F\u53E3\u7684\u65B9\u6CD5\u3002\u5176\u6B21\uFF0C\u5F15\u7528\u522B\u4EBA\u7684\u8BDD\uFF0C\u5C31\u662F\u628A\u90A3\u4E9B\u4EBA\u5728\u5FC3\u7406\u4E0A\u62C9\u5728\u4F60\u7684\u540C\u4E00\u6218\u7EBF\u3002\u8FD8\u6709\u4E2A\u597D\u5904\uFF0C\u5C31\u662F\u201C\u8BE1\u6587\u800C\u8C32\u8C0F\u201D\uFF0C\u660E\u660E\u662F\u4F60\u8BF4\u7684\uFF0C\u5374\u8BF4\u201C\u6709\u4EBA\u8FD9\u4E48\u8BF4\u201D\uFF0C\u542C\u8005\u5373\u4F7F\u4E0D\u540C\u610F\uFF0C\u8981\u53D1\u6012\uFF0C\u4E5F\u4E0D\u4F1A\u53D1\u5230\u4F60\u5934\u4E0A\uFF0C\u4F60\u53EF\u4EE5\u4E0D\u8BA4\u8D26\uFF0C\u8BF4\u201C\u522B\u4EBA\u8FD9\u4E48\u8BF4\u201D\uFF0C\u4F60\u53EA\u662F\u62FF\u6765\u53C2\u8003\u3002</p><p>\u56DB\u3001\u7126\u70B9\u8F6C\u79FB</p><p>1.\u4E00\u4E9B\u770B\u8D77\u6765\u80FD\u9AD8\u8C08\u9614\u8BBA\u7684\u4EBA\uFF0C\u4E0D\u4EC5\u4F1A\u6210\u4E3A\u4F1A\u573A\u7684\u7126\u70B9\uFF0C\u800C\u4E14\u8868\u73B0\u4E86\u4ED6\u5B66\u8BC6\u6E0A\u535A\u3002\u5B9E\u9645\u4E0A\uFF0C\u4ED6\u4EEC\u53EA\u662F\u66F4\u4F1A\u5E26\u52A8\u8BDD\u9898\uFF0C\u4ED6\u8BA9\u5927\u5BB6\u7684\u8BDD\u9898\u4E3B\u52A8\u5F15\u9886\u5230\u4ED6\u4EEC\u6700\u5185\u884C\u7684\u4E1C\u897F\u4E0A\u3002</p><p>2.\u8F6C\u79FB\u7126\u70B9\uFF0C\u901A\u5E38\u7528\u5728\u4E0D\u9700\u8981\u7ACB\u5373\u5F97\u5230\u7B54\u6848\u7684\u4E8B\u60C5\u4E0A\uFF0C\u6216\u662F\u5F53\u4F60\u53EF\u4EE5\u201C\u7528\u8F6C\u79FB\u7684\u76EE\u6807\uFF0C\u4EE3\u66FF\u90E8\u5206\u7684\u7B54\u6848\u201D\u65F6\u5019\u3002</p><p>3.\u4E00\u4E2A\u4F1A\u8BF4\u8BDD\u7684\u4EBA\uFF0C\u7EDD\u4E0D\u662F\u9760\u6492\u8C0E\uFF0C\u4ED6\u53EF\u4EE5\u8BF4\u90E8\u5206\u771F\u8BDD\uFF0C\u53EF\u4EE5\u6280\u5DE7\u5730\u628A\u8BDD\u9898\u5E26\u5F00\uFF0C\u4EE5\u514D\u53BB\u5C34\u5C2C\u573A\u9762\uFF0C\u4F46\u7EDD\u4E0D\u80FD\u663E\u793A\u51FA\u95EA\u70C1\u5176\u8BCD\u7684\u6837\u5B50\u3002</p><p>\u4E94\u3001\u6539\u5929\u5728\u8BF4\u7684\u5B66\u95EE</p><p>1.\u6311\u201C\u5409\u65F6\u201D\uFF0C\u6311\u597D\u5730\u65B9\u518D\u8BF4\uFF0C\u627E\u5230\u6700\u6070\u5F53\u3001\u5BF9\u81EA\u5DF1\u6700\u6709\u5229\u7684\u6761\u4EF6\u4E0B\u8FDB\u8A00\uFF0C\u907F\u5F00\u51F6\u65F6\u3002</p><p>2.\u201C\u6539\u5929\u518D\u8BF4\u201D\u662F\u201C\u9000\u8A00\u201D\uFF0C\u610F\u601D\u662F\u73B0\u5728\u4E0D\u65B9\u4FBF\u518D\u8BF4\uFF0C\u8FD9\u65F6\u5019\u4F60\u5E94\u5F53\u8BC6\u8DA3\uFF0C\u77E5\u9053\u4ED6\u53EF\u80FD\u6709\u96BE\u5904\u4E0D\u597D\u8BF4\u8BDD\u3002</p><p>\u516D\u3001\u201C\u8D8A\u6CA1\u5B9E\u6743\u7684\u4EBA\uFF0C\u8D8A\u8981\u865A\u8363\u201D</p><p>1.\u4EBA\u90FD\u8981\u88AB\u5C0A\u91CD\uFF0C\u5373\u4F7F\u662F\u4E00\u4E2A\u95E8\u623F\uFF0C\u4ECA\u5929\u4F60\u8BF7\u4ED6\u8FDB\u53BB\u901A\u62A5\u4E00\u58F0\uFF0C\u4E5F\u5BF9\u4ED6\u5BA2\u5BA2\u6C14\u6C14\u7684\u3002\u8D8A\u662F\u804C\u6743\u5C0F\u7684\u4EBA\u8D8A\u559C\u6B22\u5237\u5B58\u5728\u611F\uFF0C\u4ED6\u53EF\u80FD\u5C31\u662F\u529E\u516C\u5BA4\u89D2\u843D\u7BA1\u6A61\u76AE\u7AE0\u7684\uFF0C\u4F60\u4E0D\u5BA2\u6C14\u70B9\u513F\uFF0C\u4ED6\u5C31\u4F7F\u52B2\u62D6\u5EF6\u3002</p><p>2.\u6709\u591A\u5C11\u592B\u59BB\u7684\u53CD\u76EE\uFF0C\u4E0D\u90FD\u662F\u56E0\u4E3A\u592A\u592A\u8D44\u52A9\u201C\u5A18\u5BB6\u201D\uFF0C\u6216\u4E08\u592B\u5077\u5077\u52FE\u7ED3\u201C\u592B\u5BB6\u201D\u3002\u201C\u94B1\u201D\u5E38\u5E38\u662F\u6700\u5927\u7684\u539F\u56E0\u2014\u2014\u4E24\u4E2A\u4EBA\u62FC\u547D\u7701\uFF0C\u53EA\u76FC\u591A\u6709\u70B9\uFF0C\u4F60\u4E3A\u4EC0\u4E48\u8981\u628A\u94B1\u5F80\u5916\u9001\uFF1F\u4F46\u66F4\u5927\u539F\u56E0\uFF0C\u662F\u5F88\u591A\u4E08\u592B\u6216\u59BB\u5B50\uFF0C\u4E0D\u8BA9\u53E6\u4E00\u534A\u77E5\u9053\uFF0C\u800C\u5077\u5077\u7ED9\uFF0C\u8FD9\u4F1A\u8BA9\u53E6\u4E00\u534A\u6709\u7CBE\u795E\u4E0A\u88AB\u51FA\u5356\u7684\u611F\u89C9\u3002</p><p>\u4E03\u3001\u4E0D\u8981\u5403\u4EBA\u8C46\u8150</p><p>\u53D1\u73B0\u522B\u4EBA\u6492\u8C0E\uFF0C\u82E5\u975E\u62C6\u7A7F\u4E0D\u53EF\uFF0C\u5C31\u65E9\u70B9\u62C6\u7A7F\uFF0C\u514D\u5F97\u8BF4\u4E00\u534A\u518D\u62C6\u7A7F\u663E\u5F97\u4F60\u5B58\u5FC3\u770B\u4ED6\u8868\u6F14\u3002</p><p>\u516B\u3001\u6298\u51B2\u6A3D\u4FCE</p><p>1.\u6162\u6162\u71AC\uFF0C\u6162\u6162\u8017\uFF0C\u8BB8\u591A\u539F\u672C\u8C08\u4E0D\u6210\u7684\u4E8B\u5C31\u53EF\u80FD\u8C08\u6210\u3002</p><p>2.\u5BF9\u6BD4\u7684\u827A\u672F\u3002\u8BBE\u5B9A\u66F4\u5DEE\u7684\u7ED3\u679C\u4F5C\u4E3A\u95E8\u69DB\uFF0C\u518D\u8BF4\u4F18\u4E8E\u7ED3\u679C\u7684\u9014\u5F84\uFF0C\u524D\u540E\u5BF9\u6BD4\uFF0C\u8FBE\u5230\u540E\u8005\u76EE\u7684\uFF0C\u5FC3\u7406\u5B66\u4E0A\u79F0\u4E4B\u4E3A\u767B\u95E8\u69DB\u6548\u5E94\u3002</p><p>\u4E5D\u3001\u8D5E\u7F8E\u4F60\u7684\u5BF9\u624B</p><p>1.\u4F60\u8D5E\u7F8E\u4ED6\uFF0C\u4ED6\u8FD8\u5BF9\u4F60\u7A77\u8FFD\u731B\u6253\uFF0C\u522B\u4EBA\u4F1A\u600E\u4E48\u8BF4\uFF1F\u4F1A\u8BF4\u4ED6\u4E0D\u539A\u9053\u5427\u3002\u8D5E\u7F8E\u5BF9\u624B\u8FD8\u6709\u4E00\u4E2A\u597D\u5904\uFF0C\u5C31\u662F\u7ED9\u81EA\u5DF1\u633D\u56DE\u4E00\u70B9\u9762\u5B50\u3002</p><p>2.\u8D5E\u7F8E\u4F60\u7684\u654C\u4EBA\uFF0C\u662F\u4E00\u79CD\u5F88\u9AD8\u7684\u8BF4\u8BDD\u827A\u672F\uFF0C\u6700\u8D77\u7801\u4F60\u8BF4\u7684\u51FA\u53E3\uFF0C\u5C31\u8868\u793A\u7684\u4FEE\u517B\u3002</p>',66),t=[d];function i(c,n){return a(),e("div",null,t)}var o=p(h,[["render",i],["__file","\u300A\u628A\u8BDD\u8BF4\u5230\u5FC3\u7A9D\u5B50\u91CC\u300B\u8BFB\u4E66\u7B14\u8BB0.html.vue"]]);export{o as default};
