import{_ as p,o as a,c as e,a as h}from"./app.4524d535.js";const r={},d=h('<h1 id="\u300A\u83DC\u6839\u8C2D\u300B\u8BFB\u4E66\u7B14\u8BB0" tabindex="-1"><a class="header-anchor" href="#\u300A\u83DC\u6839\u8C2D\u300B\u8BFB\u4E66\u7B14\u8BB0" aria-hidden="true">#</a> \u300A\u83DC\u6839\u8C2D\u300B\u8BFB\u4E66\u7B14\u8BB0</h1><p>\u5173\u4E8E\u672C\u4E66</p><blockquote><p>\u4E66\u540D\uFF1A\u83DC\u6839\u8C2D</p><p>\u4F5C\u8005\uFF1A\u6D2A\u5E94\u660E(\u660E)</p><p>\u81EA\u6211\u8BC4\u5206\uFF1A\u2605\u2605\u2605\u2605\u2605</p><p>\u9605\u8BFB\u65F6\u95F4\uFF1A2019\u5E749\u6708</p></blockquote><h2 id="\u5185\u5BB9\u7B80\u4ECB" tabindex="-1"><a class="header-anchor" href="#\u5185\u5BB9\u7B80\u4ECB" aria-hidden="true">#</a> \u5185\u5BB9\u7B80\u4ECB</h2><p>\u6BDB\u6CFD\u4E1C\u8BF4\u8FC7\uFF1A\u54AC\u5F97\u83DC\u6839\u5219\u767E\u4E8B\u53EF\u505A\u3002\u83DC\u6839\u8C2D\u662F\u4E00\u672C\u95EE\u4EBA\u5904\u4E8B\u7684\u597D\u4E66\uFF0C\u968F\u7740\u9605\u5386\u589E\u957F\u5C31\u4F1A\u6E10\u6E10\u660E\u767D</p><h2 id="\u5185\u5BB9\u6458\u8981" tabindex="-1"><a class="header-anchor" href="#\u5185\u5BB9\u6458\u8981" aria-hidden="true">#</a> \u5185\u5BB9\u6458\u8981</h2><h3 id="\u4FEE\u7701" tabindex="-1"><a class="header-anchor" href="#\u4FEE\u7701" aria-hidden="true">#</a> \u4FEE\u7701</h3><p>1.\u5B66\u8005\u5F53\u6816\u5FC3\u7384\u9ED8\uFF0C\u4EE5\u5B81\u543E\u771F\u4F53\uFF1B\u4EA6\u5F53\u9002\u5FD7\u606C\u6109\uFF0C\u4EE5\u517B\u543E\u5706\u673A\u3002</p><p>\u70B9\u8BC4\uFF1A\u4EBA\u8981\u5B66\u4F1A\u751F\u6D3B\uFF0C\u65E2\u4E0D\u6C89\u8FF7\u58F0\u8272\u540D\u5229\uFF0C\u4E5F\u4E0D\u8981\u65E0\u6240\u8FFD\u6C42\uFF0C\u6B7B\u6C14\u6C89\u6C89\u3002\u8FD9\u6837\u624D\u80FD\u4FDD\u6301\u5185\u5FC3\u7684\u5B81\u9759\u548C\u8C10\uFF0C\u4EAB\u53D7\u751F\u6D3B\u7684\u771F\u6B63\u4E50\u8DA3\u3002</p><p>2.\u58EB\u4EBA\u6709\u767E\u6298\u4E0D\u56DE\u4E4B\u771F\u5FC3\uFF0C\u624D\u6709\u4E07\u53D8\u65E0\u7A77\u4E4B\u5999\u7528\u3002</p><p>\u70B9\u8BC4\uFF1A\u4E00\u4E2A\u4EBA\u5177\u6709\u767E\u6298\u4E0D\u6320\u7684\u521A\u6BC5\u54C1\u8D28\uFF0C\u65E0\u8BBA\u505A\u4EC0\u4E48\u4E8B\uFF0C\u4ED6\u90FD\u80FD\u77E2\u5FD7\u4E0D\u6E1D\uFF1B\u65E0\u8BBA\u5904\u4E8E\u4EC0\u4E48\u6837\u7684\u5883\u5730\uFF0C\u4ED6\u90FD\u80FD\u4ECE\u5BB9\u9762\u5BF9\uFF0C\u7EC8\u80FD\u5927\u6709\u6240\u6210\u3002</p><p>3.\u65E0\u4E8B\u4FBF\u601D\u6709\u6742\u5FF5\u5426\uFF0C\u6709\u4E8B\u4FBF\u601D\u6709\u7C97\u6D6E\u610F\u6C14\u5426\u3002\u5F97\u610F\u4FBF\u601D\u6709\u9A84\u77DC\u8F9E\u8272\u5426\uFF0C\u5931\u610F\u4FBF\u601D\u6709\u6028\u671B\u5FD8\u60C5\u5426\u3002</p><p>4.\u8EAB\u4E0D\u5B9C\u5FD9\uFF0C\u800C\u5FD9\u4E8E\u95F2\u6687\u4E4B\u65F6\uFF0C\u4EA6\u53EF\u8B66\u60D5\u60F0\u6C14\uFF1B\u5FC3\u4E0D\u53EF\u653E\uFF0C\u800C\u653E\u4E8E\u624B\u6444\u4E4B\u540E\uFF0C\u4EA6\u53EF\u9F13\u7545\u5929\u673A\u3002</p><p>\u70B9\u8BC4\uFF1A\u4E45\u95F2\u5BB9\u6613\u4EA7\u751F\u60F0\u6027\uFF0C\u4E00\u5473\u6536\u655B\u59A8\u788D\u4EBA\u7684\u5929\u8D4B\u7684\u53D1\u5C55\u3002\u56E0\u6B64\uFF0C\u95F2\u9759\u8981\u9002\u5EA6\uFF0C\u6536\u655B\u4E5F\u8981\u9002\u5EA6\uFF0C\u624D\u6709\u5229\u4E8E\u53D1\u6325\u4EBA\u7684\u5929\u8D4B\u624D\u80FD\u3002</p><p>5.\u5B66\u8005\u4E0D\u60A3\u57A2\u75C5\uFF0C\u800C\u60A3\u6D01\u75C5\u4E4B\u96BE\u6CBB\uFF1B\u4E0D\u754F\u4E8B\u969C\uFF0C\u800C\u754F\u7406\u969C\u4E4B\u96BE\u9664\u3002</p><p>\u70B9\u8BC4\uFF1A\u5916\u6765\u7684\u7269\u6B32\u5229\u8BF1\u5E76\u4E0D\u53EF\u6015\uFF0C\u504F\u6267\u9519\u8BEF\u7684\u770B\u6CD5\u624D\u6700\u4E3A\u53EF\u6015\uFF0C\u601D\u60F3\u89C2\u5FF5\u4E00\u65E6\u6839\u6DF1\u8482\u56FA\uFF0C\u662F\u5F88\u96BE\u6539\u53D8\u7684\u3002</p><p>6.\u9762\u4E0A\u626B\u5F00\u5341\u5C42\u7532\uFF0C\u7709\u76EE\u624D\u53EF\u65E0\u618E\uFF1B\u80F8\u4E2D\u6DA4\u53BB\u6570\u6597\u751F\uFF0C\u8BED\u8A00\u65B9\u89C9\u6709\u5473\u3002</p><p>\u70B9\u8BC4\uFF1A\u4EBA\u8981\u6709\u5EC9\u803B\u4E4B\u5FC3\uFF0C\u4E00\u4E2A\u4EBA\u6709\u4E86\u5EC9\u803B\u611F\uFF0C\u4ED6\u5C31\u4F1A\u5BF9\u81EA\u5DF1\u5BF9\u8A00\u884C\u4E3E\u6B62\u5F88\u68C0\u70B9\uFF0C\u4E3A\u4EBA\u5904\u4E8B\u80FD\u8D62\u5F97\u4EBA\u5FC3\u3002</p><p>7.\u4E00\u52FA\u6C34\u4FBF\u5177\u6709\u56DB\u6D77\u6C34\u5473\uFF0C\u4E16\u6CD5\u4E0D\u5FC5\u5C3D\u5C1D\uFF1B\u5343\u6C5F\u6708\u603B\u662F\u4E00\u8F6E\u6708\u5149\uFF0C\u5FC3\u73E0\u5B9C\u5F53\u72EC\u90CE\u3002</p><p>\u70B9\u8BC4\uFF1A\u4E0D\u5FC5\u4EBA\u4EBA\u90FD\u5F97\u53BB\u4EA4\u5F80\uFF0C\u4E8B\u4E8B\u90FD\u5F97\u53BB\u4EB2\u8EAB\u7ECF\u5386\u3002\u4E16\u95F4\u6ECB\u5473\u7684\u5C1D\u77E5\uFF0C\u4EBA\u751F\u771F\u8C1B\u7684\u83B7\u5F97\u4E3B\u8981\u5728\u4E8E\u4FDD\u6301\u5FC3\u5883\u6F84\u6E05\uFF0C\u8981\u7528\u5FC3\u53BB\u4F53\u609F\u3002</p><p>8.\u5F97\u610F\u5904\u8BBA\u5730\u8C08\u5929\uFF0C\u4FF1\u662F\u6C34\u5E95\u635E\u6708\uFF1B\u62C2\u610F\u65F6\u541E\u51B0\u556E\u96EA\uFF0C\u624D\u4E3A\u706B\u5185\u683D\u83B2\u3002</p><p>9.\u4E00\u5FF5\u8FC7\u5DEE\uFF0C\u8DB3\u4E27\u751F\u5E73\u4E4B\u5584\uFF1B\u7EC8\u8EAB\u68C0\u996C\uFF0C\u96BE\u76D6\u4E00\u4E8B\u4E4B\u6106\u3002</p><h3 id="\u5E94\u916C" tabindex="-1"><a class="header-anchor" href="#\u5E94\u916C" aria-hidden="true">#</a> \u5E94\u916C</h3><p>1.\u501A\u9AD8\u624D\u800C\u73A9\u4E16\uFF0C\u80CC\u540E\u987B\u9632\u5C04\u5F71\u4E4B\u866B\uFF1B\u9970\u539A\u8C8C\u4EE5\u6B3A\u4EBA\uFF0C\u9762\u524D\u6050\u6709\u7167\u80C6\u4E4B\u955C\u3002</p><p>\u70B9\u8BC4\uFF1A\u6770\u51FA\u5BB9\u6613\u62DB\u4ED6\u4EBA\u5AC9\u5992</p><p>2.\u5FC3\u4F53\u6F84\u6F88\uFF0C\u5E38\u5728\u660E\u955C\u6B62\u6C34\u4E4B\u4E2D\uFF0C\u5219\u5929\u4E0B\u81EA\u65E0\u53EF\u538C\u4E4B\u4E8B\uFF1B\u610F\u6C14\u548C\u5E73\uFF0C\u5E38\u5728\u4E3D\u65E5\u5149\u98CE\u4E4B\u5185\uFF0C\u5219\u5929\u4E0B\u81EA\u65E0\u53EF\u6076\u4E4B\u4EBA\u3002</p><p>\u70B9\u8BC4\uFF1A\u629B\u5F00\u4E16\u4FD7\u7684\u5404\u79CD\u601D\u8651\uFF0C\u8131\u4FD7\u540E\u7684\u5FC3\u5883\u660E\u6F88\uFF0C\u610F\u6C14\u5E73\u548C\uFF0C\u6240\u89C2\u7167\u7684\u4FBF\u662F\u4E00\u4E2A\u4EBA\u4E0E\u81EA\u7136\u548C\u8C10\u7684\u7406\u60F3\u5316\u7684\u5883\u754C\u3002</p><p>3.\u4F3A\u5BDF\u4EE5\u4E3A\u660E\u8005\uFF0C\u5E38\u56E0\u660E\u800C\u751F\u6697\uFF0C\u6545\u541B\u5B50\u4EE5\u606C\u517B\u667A\uFF1B\u594B\u8BAF\u4EE5\u4E3A\u901F\u8005\uFF0C\u591A\u56E0\u901F\u800C\u81F4\u8FDF\uFF0C\u6545\u541B\u5B50\u4EE5\u91CD\u6301\u8F7B\u3002</p><p>\u70B9\u8BC4\uFF1A\u4E3A\u4EBA\u5904\u4E16\u4E0D\u8981\u592A\u950B\u8292\u6BD5\u9732\uFF0C\u4FDD\u6301\u5FC3\u5883\u606C\u9759\u5E76\u4EE5\u6B64\u6765\u84C4\u517B\u667A\u6167\u3002</p><p>4.\u9047\u5927\u4E8B\u77DC\u6301\u8005\uFF0C\u5C0F\u4E8B\u5FC5\u7EB5\u5F1B\uFF1B\u5904\u660E\u5EAD\u68C0\u996C\u8005\uFF0C\u6697\u5BA4\u5FC5\u653E\u9038\u3002</p><p>\u70B9\u8BC4\uFF1A\u5927\u4E8B\u5C0F\u4E8B\u90FD\u8FC7\u95EE\uFF0C\u516C\u4E8B\u79C1\u4E8B\u90FD\u7D27\u6293\uFF0C\u5BB9\u6613\u5BFC\u81F4\u788C\u800C\u65E0\u5927\u4E3A\uFF0C\u52B3\u800C\u65E0\u5927\u529F\u3002</p><p>5.\u4E0E\u4EBA\u8005\uFF0C\u4E0E\u5176\u6613\u758F\u4E8E\u7EC8\uFF0C\u4E0D\u82E5\u96BE\u4EB2\u4E8E\u59CB\uFF1B\u5FA1\u4E8B\u8005\uFF0C\u4E0E\u5176\u5DE7\u6301\u4E8E\u540E\uFF0C\u4E0D\u82E5\u62D9\u5B88\u4E8E\u524D\u3002</p><p>\u70B9\u8BC4\uFF1A\u4E0E\u4EBA\u4EA4\u5F80\uFF0C\u9700\u8981\u6709\u4E00\u4E2A\u5F7C\u6B64\u4E86\u89E3\u7684\u8FC7\u7A0B\u3002\u5728\u6CA1\u6709\u4E86\u89E3\u5BF9\u65B9\u4EE5\u524D\uFF0C\u4E0D\u8981\u8F7B\u6613\u5730\u4E0E\u4E4B\u8FC7\u4E8E\u4EB2\u8FD1\u3002</p><p>6.\u62D6\u6CE5\u5E26\u6C34\u4E4B\u7D2F\uFF0C\u75C5\u6839\u5728\u4E00\u604B\u5B57\uFF1B\u968F\u65B9\u9010\u5706\u4E4B\u5999\uFF0C\u4FBF\u5B9C\u5728\u4E00\u8010\u5B57\u3002</p><p>\u70B9\u8BC4\uFF1A\u604B\u4EC0\u4E48\u672C\u65E0\u53EF\u539A\u975E\uFF0C\u4F46\u8981\u6709\u5206\u5BF8\uFF0C\u4E0D\u53EF\u8D2A\u604B\uFF0C\u8D2A\u604B\u4F1A\u52B3\u8D39\u5FC3\u795E\u3002</p><p>7.\u9080\u5343\u767E\u4EBA\u4E4B\u6B22\uFF0C\u4E0D\u5982\u91CA\u4E00\u4EBA\u4E4B\u6028\uFF1B\u5E0C\u5343\u767E\u4E8B\u4E4B\u8363\uFF0C\u4E0D\u5982\u514D\u4E00\u4E8B\u4E4B\u4E11\u3002</p><p>8.\u5E08\u53E4\u4E0D\u5E08\u4ECA\uFF0C\u820D\u4E3E\u4E16\u5171\u8D8B\u4E4B\u8F99\uFF1B\u4F9D\u6CD5\u4E0D\u4F9D\u4EBA\uFF0C\u5C0A\u65F6\u8C6A\u803B\u95EE\u4E4B\u9014\u3002</p><p>\u70B9\u8BC4\uFF1A\u4E00\u4E2A\u4EBA\u8981\u5584\u4E8E\u5B66\u4E60\u548C\u601D\u8003\uFF0C\u5BF9\u73B0\u5B9E\u4E2D\u90A3\u4E9B\u65F6\u5C1A\u7684\u4E1C\u897F\u4E0D\u76F2\u4ECE\uFF0C\u6562\u4E8E\u7591\u4ECA\u3002</p><p>9.\u5904\u4E16\u800C\u6B32\u4EBA\u611F\u6069\uFF0C\u4FBF\u4E3A\u655B\u6028\u4E4B\u9053\uFF1B\u9047\u4E8B\u800C\u4E3A\u4EBA\u9664\u5BB3\uFF0C\u5373\u662F\u5BFC\u5229\u4E4B\u673A\u3002</p><p>\u70B9\u8BC4\uFF1A\u4FA0\u4E49\u4E4B\u4E3E\u80FD\u8D62\u5F97\u4EBA\u5FC3\uFF0C\u8D62\u4EBA\u5FC3\u4FBF\u6709\u52A9\u4E8E\u81EA\u5DF1\u4E8B\u4E1A\u6210\u529F\u3002</p><p>10.\u53E3\u4E2D\u5723\u8D24\uFF0C\u5FC3\u4E2D\u6208\u5251\uFF0C\u529D\u4EBA\u800C\u4E0D\u529D\u5DF1\uFF0C\u540D\u4E3A\u6302\u699C\u4FEE\u884C\uFF1B\u8BFB\u614E\u887E\u5F71\uFF0C\u9634\u60DC\u5206\u5BF8\uFF0C\u7ADE\u5904\u800C\u590D\u7ADE\u65F6\uFF0C\u624D\u662F\u6709\u6839\u5B66\u95EE\u3002</p><p>11.\u8D39\u5343\u91D1\u800C\u7ED3\u7EB3\u8D24\u8C6A\uFF0C\u5B70\u82E5\u503E\u534A\u74E2\u4E4B\u6817\u4EE5\u6D4E\u9965\u997F\u4E4B\u4EBA\uFF1B\u6784\u5343\u6979\u800C\u62DB\u6765\u5BBE\u5BA2\uFF0C\u5B70\u82E5\u847A\u6570\u693D\u4E4B\u8305\u4EE5\u5E87\u5B64\u5BD2\u4E4B\u58EB\u3002</p><p>\u70B9\u8BC4\uFF1A\u82B1\u8D39\u5927\u91CF\u94B1\u8D22\u7528\u4E8E\u7ED3\u4EA4\u540D\u6D41\u663E\u8D35\uFF0C\u8FD8\u4E0D\u5982\u6D4E\u8D2B\u5E2E\u52A9\u90A3\u4E9B\u6682\u65F6\u843D\u9B44\u7684\u4EBA\u3002\u9760\u91D1\u94B1\u6536\u4E70\u7684\u53CB\u60C5\u662F\u9760\u4E0D\u4F4F\u7684\uFF0C\u4E00\u65E6\u6CA1\u4E86\u94B1\u8D22\uFF0C\u53CB\u60C5\u81EA\u7136\u4F1A\u6DE1\u53BB\u3002\u5E2E\u52A9\u843D\u9B44\u8005\u4E5F\u7B49\u4E8E\u5E2E\u52A9\u81EA\u5DF1\u3002</p><p>12.\u89E3\u6597\u8005\uFF0C\u52A9\u4E4B\u4EE5\u5A01\u5219\u6012\u6C14\u81EA\u5E73\uFF1B\u60E9\u8D2A\u8005\uFF0C\u6D4E\u4E4B\u4EE5\u6B32\u5219\u5229\u5FC3\u53CD\u8C08\u3002\u6240\u8C13\u56E0\u5176\u52BF\u800C\u5229\u5BFC\u4E4B\uFF0C\u4EA6\u6551\u65F6\u5E94\u53D8\u4E00\u6743\u5B9C\u6CD5\u4E5F\u3002</p><p>\u70B9\u8BC4\uFF1A\u6012\u6C14\u4E0D\u80FD\u8FC7\u76DB\uFF0C\u7269\u6B32\u4E4B\u5FC3\u4E0D\u53EF\u8FC7\u91CD\u3002\u5982\u679C\u5F3A\u884C\u904F\u5236\u6012\u3001\u6B32\uFF0C\u7ED3\u679C\u53EF\u80FD\u4F1A\u9002\u5F97\u5176\u53CD\u3002\u5E73\u6012\u3001\u60E9\u8D2A\u6700\u597D\u56E0\u52BF\u5229\u5BFC\uFF0C\u7ED9\u6012\u8005\u63D0\u4F9B\u5BA3\u6CC4\u6124\u6012\u7684\u673A\u4F1A\uFF0C\u9002\u5F53\u6EE1\u8DB3\u4EBA\u4EEC\u7684\u7269\u6B32\u3002</p><h3 id="\u8BC4\u8BAE" tabindex="-1"><a class="header-anchor" href="#\u8BC4\u8BAE" aria-hidden="true">#</a> \u8BC4\u8BAE</h3><p>1.\u5927\u6076\u591A\u4ECE\u67D4\u5904\u4F0F\uFF0C\u54F2\u58EB\u987B\u9632\u7EF5\u91CC\u85CF\u9488\uFF1B\u6DF1\u4EC7\u5E38\u81EA\u7231\u4E2D\u6765\uFF0C\u8FBE\u4EBA\u5B9C\u8FDC\u5200\u5934\u4E4B\u871C\u3002</p><p>\u70B9\u8BC4\uFF1A\u58F0\u8272\u94B1\u8D22\u6784\u7B51\u7684\u6E29\u67D4\u4E61\u3001\u7231\u604B\u6CB3\u867D\u7136\u9189\u8FF7\u4EBA\uFF0C\u8EAB\u5904\u5176\u4E2D\u597D\u505A\u68A6\uFF0C\u4F46\u5F80\u5F80\u9690\u85CF\u8005\u6DF1\u91CD\u7F6A\u6076\u3002</p><p>2.\u65E0\u7406\u5916\u81F3\uFF0C\u5F53\u5982\u9022\u864E\u800C\u6DF1\u907F\uFF0C\u52FF\u6043\u683C\u517D\u4E4B\u80FD\uFF1B\u5984\u5FF5\u5185\u5174\uFF0C\u4E14\u62DF\u63A2\u6C64\u800C\u75BE\u7981\uFF0C\u83AB\u7EB5\u67D3\u6307\u4E4B\u6B32\u3002</p><p>\u70B9\u8BC4\uFF1A\u65E0\u7406\u4E4B\u5F92\u5927\u591A\u72C2\u5984\uFF0C\u8DDF\u4ED6\u4EEC\u5F3A\u884C\u8BBA\u7406\u662F\u5F92\u52B3\u7684\uFF0C\u5F04\u4E0D\u597D\u8FD8\u4F1A\u62DB\u81F4\u4E0D\u5FC5\u8981\u7684\u4EBA\u8EAB\u4F24\u5BB3\u3002\u81EA\u5DF1\u54C1\u884C\u7AEF\u6B63\uFF0C\u4E0D\u53EF\u505A\u975E\u5206\u7684\u4E8B\u60C5\u3002</p><p>3.\u5931\u8840\u4E8E\u6000\u4E2D\uFF0C\u582A\u7B11\u7329\u7329\u4E4B\u55DC\u9152\uFF1B\u4E3A\u5DE2\u4E8E\u5E55\u4E0A\uFF0C\u53EF\u601C\u71D5\u71D5\u4E4B\u5077\u5B89\u3002</p><p>\u70B9\u8BC4\uFF1A\u4EBA\u82E5\u4E00\u5473\u8D2A\u604B\u773C\u524D\u7684\u5229\u76CA\uFF0C\u8D2A\u604B\u773C\u524D\u7684\u5B89\u9038\uFF0C\u540C\u6837\u4F1A\u4F7F\u81EA\u5DF1\u9677\u5165\u4E0D\u5229\u6216\u5371\u9669\u7684\u5883\u5730\u3002</p><p>4.\u8F66\u4E89\u9669\u9053\uFF0C\u9A6C\u9A8B\u5148\u97AD\uFF0C\u5230\u8D25\u5904\u672A\u514D\u566C\u8110\uFF1B\u6817\u559C\u5806\u5C71\uFF0C\u91D1\u5938\u8FC7\u6597\uFF0C\u4E34\u884C\u65F6\u8FD8\u662F\u7A7A\u624B\u3002</p><p>\u70B9\u8BC4\uFF1A\u80DC\u8D1F\u56FA\u7136\u53EF\u4E89\uFF0C\u4F46\u4E0D\u8981\u592A\u6025\u4E8E\u6C42\u6210\uFF0C\u4E0D\u987E\u540E\u679C\u5730\u4E3A\u4E4B\u62FC\u547D\uFF0C\u5F80\u5F80\u5BFC\u81F4\u5931\u8D25\uFF0C\u8FFD\u6094\u83AB\u53CA\u3002\u7269\u8D28\u8D22\u5BCC\u518D\u591A\uFF0C\u4F46\u7EC8\u7A76\u662F\u8EAB\u5916\u4E4B\u7269\uFF0C\u751F\u4E0D\u80FD\u5E26\u6765\uFF0C\u6B7B\u4E0D\u80FD\u5E26\u53BB\u3002</p><p>5.\u591A\u8F7D\u6843\u674E\u5C11\u8F7D\u8346\uFF0C\u4FBF\u662F\u5F00\u6761\u798F\u8DEF\uFF1B\u4E0D\u79EF\u8BD7\u4E66\u504F\u79EF\u7389\uFF0C\u8FD8\u5982\u7B51\u4E2A\u7978\u57FA\u3002</p><p>\u70B9\u8BC4\uFF1A\u4E0E\u4EBA\u4EA4\u5F80\uFF0C\u8981\u5BBD\u5BB9\u5927\u5EA6\uFF0C\u5C3D\u53EF\u80FD\u4E0D\u7ED9\u81EA\u5DF1\u6811\u654C\u3002\u8981\u8BA9\u77E5\u8BC6\u88C5\u5907\u81EA\u5DF1\u800C\u4E0D\u8BA9\u91D1\u94B1\u6DF9\u6CA1\u81EA\u5DF1\u3002</p><p>6.\u592A\u806A\u660E\u7684\u4EBA\uFF0C\u5C0F\u4E8B\u5FC5\u6726\u80E7\uFF1B\u5927\u61F5\u61C2\u7684\u4EBA\uFF0C\u5C0F\u4E8B\u5FC5\u4F3A\u5BDF\u3002\u76D6\u4F3A\u5BDF\u4E43\u61F5\u61C2\u4E4B\u6068\uFF0C\u800C\u6726\u80E7\u6B63\u806A\u660E\u4E4B\u7A9F\u4E5F\u3002</p><p>\u70B9\u8BC4\uFF1A\u5927\u4E8B\u4E0A\u6E05\u695A\u660E\u4E86\uFF0C\u80FD\u6761\u5206\u7F15\u6790\uFF0C\u6BEB\u4E0D\u542B\u7CCA\uFF1B\u4F46\u5BF9\u5C0F\u4E8B\u5374\u201C\u7CCA\u6D82\u201D\uFF0C\u65E0\u6240\u8C13\u3002</p><h3 id="\u95F2\u9002" tabindex="-1"><a class="header-anchor" href="#\u95F2\u9002" aria-hidden="true">#</a> \u95F2\u9002</h3><p>1.\u4E16\u4E8B\u5982\u68CB\u5C40\uFF0C\u4E0D\u8457\u7684\u624D\u662F\u9AD8\u624B\uFF1B\u4EBA\u751F\u4F3C\u74E6\u76C6\uFF0C\u6253\u7834\u4E86\u65B9\u89C1\u771F\u7A7A\u3002</p><p>2.\u6D6E\u751F\u53EF\u89C1\uFF0C\u5982\u68A6\u5E7B\u6CE1\u5F71\uFF0C\u867D\u6709\u8C61\u800C\u7EC8\u65E0\u3002\u5999\u672C\u96BE\u7A77\uFF0C\u8C13\u771F\u4FE1\u7075\u660E\uFF0C\u867D\u65E0\u8C61\u800C\u5E38\u6709\u3002</p><p>\u70B9\u8BC4\uFF1A\u4EBA\u4E16\u6D6E\u534E\u540D\u5229\u5766\u9732\u4E8E\u4F17\uFF0C\u4E0D\u8FC7\u5982\u955C\u4E2D\u5F71\u8C61\u822C\u865A\u5E7B\uFF0C\u6700\u7EC8\u4F1A\u6D88\u5931\u3002\u800C\u4EBA\u751F\u9690\u6DB5\u7684\u672C\u771F\uFF0C\u867D\u7136\u65E0\u5F62\u4F46\u5374\u603B\u662F\u5B58\u5728\u7684\uFF0C\u53EA\u4E0D\u8FC7\u8981\u4EBA\u53BB\u7528\u5FC3\u9886\u609F\u3002</p><h3 id="\u6982\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u6982\u8BBA" aria-hidden="true">#</a> \u6982\u8BBA</h3><p>1.\u653B\u4EBA\u4E4B\u6076\u6BCB\u592A\u4E25\uFF0C\u8981\u601D\u5176\u582A\u53D7\uFF1B\u6559\u4EBA\u4EE5\u5584\u6BCB\u8FC7\u9AD8\uFF0C\u5F53\u4F7F\u5176\u53EF\u4ECE\u3002</p><p>\u70B9\u8BC4\uFF1A\u6279\u8BC4\u522B\u4EBA\u7684\u8FC7\u9519\u4E0D\u80FD\u592A\u82DB\u523B\uFF0C\u8981\u6CE8\u610F\u4FDD\u62A4\u522B\u4EBA\u7684\u81EA\u5C0A\u5FC3\u3002\u529D\u5BFC\u522B\u4EBA\u884C\u5584\uFF0C\u8981\u6C42\u4E0D\u8981\u592A\u9AD8\uFF0C\u5E94\u8003\u8651\u5BF9\u65B9\u662F\u5426\u6709\u80FD\u529B\u505A\u5230\u3002</p><p>2.\u5FE7\u52E4\u662F\u7F8E\u5FB7\uFF0C\u592A\u82E6\u5219\u65E0\u4EE5\u9002\u6027\u6021\u60C5\uFF1B\u6DE1\u6CCA\u662F\u9AD8\u98CE\uFF0C\u592A\u67AF\u5219\u65E0\u4EE5\u6D4E\u4EBA\u5229\u7269\u3002</p><p>\u70B9\u8BC4\uFF1A\u4F7F\u81EA\u5DF1\u5FC3\u529B\u4EA4\u7601\uFF0C\u4EBA\u4F1A\u6D3B\u5F97\u5F88\u82E6\u5F88\u7D2F\uFF0C\u5168\u7136\u4F53\u4F1A\u4E0D\u5230\u751F\u6D3B\u7684\u4E50\u8DA3\u3002</p><p>3.\u6211\u6709\u529F\u4E8E\u4EBA\u4E0D\u53EF\u5FF5\uFF0C\u800C\u8FC7\u5219\u4E0D\u53EF\u4E0D\u5FF5\uFF1B\u4EBA\u6709\u6069\u4E8E\u6211\u4E0D\u53EF\u5FD8\uFF0C\u800C\u6028\u5219\u4E0D\u53EF\u4E0D\u5FD8\u3002</p><p>4.\u82E6\u5FC3\u4E2D\u5E38\u5F97\u60A6\u5FC3\u4E4B\u8DA3\uFF0C\u5F97\u610F\u65F6\u4FBF\u751F\u5931\u610F\u4E4B\u60B2\u3002</p><p>\u70B9\u8BC4\uFF1A\u82E6\u5FC3\u8FFD\u6C42\u80FD\u5728\u82E6\u4E2D\u5F97\u5230\u4E50\u8DA3\uFF0C\u56E0\u4E3A\u611F\u53D7\u5230\u8FFD\u6C42\u6210\u529F\u7684\u559C\u60A6\uFF1B\u800C\u5F97\u610F\u65F6\u5374\u6F5C\u85CF\u5931\u610F\u7684\u4F24\u611F\uFF0C\u56E0\u4E3A\u9762\u4E34\u7740\u5927\u559C\u8FC7\u540E\u7684\u6E05\u51B7\u3002</p><p>5.\u4EBA\u77E5\u540D\u4F4D\u4E3A\u4E50\uFF0C\u4E0D\u77E5\u65E0\u540D\u65E0\u4F4D\u4E4B\u4E50\u4E3A\u6700\u771F\uFF1B\u4EBA\u77E5\u9965\u5BD2\u4E3A\u5FE7\uFF0C\u4E0D\u77E5\u4E0D\u9965\u4E0D\u5BD2\u4E4B\u5FE7\u4E3A\u66F4\u751A\u3002</p><p>\u70B9\u8BC4\uFF1A\u4E00\u822C\u4EBA\u53EA\u77E5\u9053\u5403\u4E0D\u9971\u7A7F\u4E0D\u6696\u4EE4\u4EBA\u5FE7\u8651\uFF0C\u5374\u4E0D\u77E5\u8863\u98DF\u65E0\u5FE7\u800C\u7CBE\u795E\u7A7A\u865A\u66F4\u4E3A\u75DB\u82E6\uFF0C\u4EBA\u5E94\u8BE5\u591A\u7ED9\u81EA\u5DF1\u8865\u5145\u7CBE\u795E\u98DF\u7CAE\u3002</p><p>6.\u5730\u4E4B\u79FD\u8005\u591A\u751F\u7269\uFF0C\u6C34\u4E4B\u6E05\u8005\u5E38\u65E0\u9C7C\u3002\u6545\u541B\u5B50\u5F53\u5B58\u542B\u57A2\u7EB3\u6C61\u4E4B\u91CF\uFF0C\u4E0D\u53EF\u6301\u597D\u6D01\u72EC\u884C\u4E4B\u64CD\u3002</p><p>\u70B9\u8BC4\uFF1A\u6709\u5FB7\u884C\u7684\u4EBA\u4E0D\u5E94\u8BE5\u81EA\u547D\u6E05\u9AD8\uFF0C\u8FC7\u4E8E\u6D01\u8EAB\u81EA\u597D\u800C\u81EA\u6211\u5C01\u95ED\uFF0C\u5E94\u8BE5\u6709\u5305\u5BB9\u4ED6\u4EBA\u7F3A\u70B9\u548C\u5BBD\u6055\u4ED6\u4EBA\u8FC7\u5931\u7684\u96C5\u91CF\u6C14\u5EA6\u3002</p><p>7.\u56FE\u672A\u5C31\u4E4B\u529F\uFF0C\u4E0D\u5982\u4FDD\u5DF2\u6210\u4E4B\u4E1A\uFF1B\u6094\u65E2\u5F80\u4E4B\u5931\uFF0C\u4EA6\u8981\u9632\u5C06\u6765\u4E4B\u975E\u3002</p><p>\u70B9\u8BC4\uFF1A\u4E00\u4E2A\u4EBA\u521B\u4E1A\u8981\u91CF\u529B\u800C\u884C\uFF0C\u4E0D\u8981\u53BB\u8C0B\u5212\u4E0D\u80FD\u5B8C\u6210\u7684\u4E8B\u4E1A\uFF1B\u5DF2\u6210\u7684\u4E8B\u4E1A\u8981\u52AA\u529B\u5B88\u4F4F\u3002\u4E0D\u8981\u4E00\u5473\u8FFD\u6094\u8FC7\u53BB\u7684\u5931\u8BEF\uFF0C\u8981\u201C\u65E2\u5F80\u4E0D\u548E\u201D\uFF0C\u96C6\u4E2D\u65F6\u95F4\u3001\u7CBE\u529B\u9632\u6B62\u5C06\u6765\u53EF\u80FD\u53D1\u751F\u7684\u9519\u8BEF\uFF0C\u8FD9\u624D\u662F\u660E\u667A\u4E4B\u4E3E\u3002</p><p>8.\u95F2\u4E2D\u4E0D\u653E\u8FC7\uFF0C\u5FD9\u4E2D\u6709\u53D7\u7528\uFF1B\u9759\u4E2D\u4E0D\u843D\u7A7A\uFF0C\u52A8\u4E2D\u6709\u53D7\u7528\uFF1B\u6697\u4E2D\u4E0D\u6B3A\u9690\uFF0C\u660E\u4E2D\u6709\u53D7\u7528\u3002</p><p>\u70B9\u8BC4\uFF1A\u5B89\u9759\u95F2\u9002\u65F6\u4E0D\u8BA9\u65F6\u5149\u8F7B\u6613\u6D41\u8FC7\uFF0C\u6293\u7D27\u65F6\u95F4\u505A\u4E9B\u4E8B\u60C5\uFF0C\u5145\u5B9E\u81EA\u5DF1\u7684\u7CBE\u795E\u751F\u6D3B\uFF0C\u7B49\u5230\u7E41\u5FD9\u7D27\u5F20\u65F6\u81EA\u7136\u4F1A\u6709\u5F88\u591A\u53D7\u76CA</p><p>9.\u8001\u6765\u75BE\u75C5\uFF0C\u90FD\u662F\u5C11\u65F6\u62DB\u7684\uFF1B\u8870\u65F6\u7F6A\u5B7D\uFF0C\u90FD\u662F\u76DB\u65F6\u4F5C\u7684\u3002\u6545\u6301\u76C8\u5C65\u6EE1\uFF0C\u541B\u5B50\u5C24\u5162\u5162\u7109\u3002</p><p>\u70B9\u8BC4\uFF1A\u8EAB\u4F53\u662F\u4E00\u4E2A\u4EBA\u4E00\u751F\u5FEB\u4E50\u8F9B\u798F\u7684\u524D\u63D0\u3002\u5E74\u8F7B\u65F6\u5EFA\u529F\u7ACB\u4E1A\uFF0C\u5FC5\u987B\u6CE8\u610F\u4FDD\u91CD\u8EAB\u4F53\uFF0C\u4E0D\u8981\u900F\u652F\u81EA\u5DF1\u7684\u5065\u5EB7\u3002\u5728\u767B\u4E0A\u6210\u529F\u7684\u5DC5\u5CF0\u65F6\uFF0C\u4E0D\u8981\u5FD8\u4E4E\u6240\u4EE5\uFF0C\u8981\u4F7F\u5934\u8111\u4FDD\u6301\u51B7\u9759\uFF1B\u56E0\u4E3A\u7A0D\u6709\u4E0D\u614E\uFF0C\u5C31\u6709\u6454\u4E0B\u7684\u5371\u9669\u3002</p><p>10.\u5904\u7236\u5144\u9AA8\u8089\u4E4B\u53D8\uFF0C\u5B9C\u4ECE\u5BB9\uFF0C\u4E0D\u5B9C\u6FC0\u70C8\uFF1B\u9047\u670B\u53CB\u4EA4\u6E38\u4E4B\u5931\uFF0C\u5B9C\u5240\u5207\uFF0C\u4E0D\u5B9C\u4F18\u6E38\u3002</p><p>11.\u6BCB\u504F\u4FE1\u800C\u4E3A\u5978\u6240\u6B3A\uFF0C\u6BCB\u81EA\u4EFB\u800C\u4E3A\u6C14\u6240\u4F7F\uFF1B\u6BCB\u4EE5\u5DF1\u4E4B\u957F\u800C\u5F62\u4EBA\u4E4B\u77ED\uFF0C\u6BCB\u4EE5\u5DF1\u4E4B\u62D9\u800C\u5FCC\u4EBA\u4E4B\u80FD\u3002</p><p>\u70B9\u8BC4\uFF1A\u81EA\u5DF1\u72EC\u5230\u89C1\u89E3\u53EA\u8981\u6B63\u786E\uFF0C\u5373\u4FBF\u4E0D\u88AB\u5927\u5BB6\u8BA4\u53EF\uFF0C\u4E5F\u8981\u575A\u6301\uFF1B\u4F46\u4E0D\u80FD\u56FA\u6267\u5DF1\u89C1\uFF0C\u522B\u4EBA\u7684\u610F\u89C1\u4E5F\u8981\u5584\u4E8E\u542C\u53D6\u3002</p><p>12.\u5FF5\u5934\u660F\u6563\u5904\uFF0C\u8981\u77E5\u63D0\u9192\uFF1B\u5FF5\u5934\u5403\u7D27\u65F6\uFF0C\u8981\u77E5\u653E\u4E0B\u3002\u4E0D\u7136\uFF0C\u6050\u660F\u660F\u4E4B\u75C5\uFF0C\u53C8\u6765\u61A7\u61A7\u4E4B\u6270\u77E3\u3002</p><p>\u70B9\u8BC4\uFF1A\u5584\u4E8E\u4F11\u606F\u7684\u4EBA\u624D\u5584\u4E8E\u5B66\u4E60\u548C\u5DE5\u4F5C\uFF0C\u5FC5\u987B\u52B3\u9038\u7ED3\u5408\uFF0C\u6709\u5F20\u6709\u5F1B\u3002</p><p>13.\u6069\u5B9C\u81EA\u6DE1\u800C\u6D53\uFF0C\u5148\u6D53\u540E\u6DE1\u8005\uFF0C\u4EBA\u5FD8\u5176\u60E0\uFF1B\u5A01\u5B9C\u81EA\u4E25\u800C\u5BBD\uFF0C\u5148\u5BBD\u540E\u4E25\u8005\uFF0C\u4EBA\u6028\u5176\u9177\u3002</p><p>14.\u5904\u5B89\u4E50\u4E4B\u573A\uFF0C\u5F53\u4F53\u60A3\u96BE\u666F\u51B5\uFF1B\u7ACB\u65C1\u89C2\u4E4B\u5730\uFF0C\u8981\u77E5\u5F53\u5C40\u82E6\u8877\uFF1B\u7406\u73B0\u6210\u4E4B\u4E8B\uFF0C\u5B9C\u5BA1\u521B\u59CB\u8270\u8F9B\u3002</p><p>15.\u4E8B\u7A0D\u62C2\u9006\uFF0C\u4FBF\u601D\u4E0D\u5982\u6211\u7684\u4EBA\uFF0C\u5219\u6028\u5C24\u81EA\u6D88\uFF1B\u5FC3\u7A0D\u6020\u8352\uFF0C\u4FBF\u601D\u80DC\u4F3C\u6211\u7684\u4EBA\uFF0C\u5219\u7CBE\u795E\u81EA\u594B\u3002</p><p>\u70B9\u8BC4\uFF1A\u4EBA\u5728\u4E0D\u987A\u65F6\u8981\u8C03\u6574\u81EA\u5DF1\u7684\u5FC3\u6001\u3002</p><p>16.\u4F18\u4EBA\u5085\u7C89\u8C03\u6731\uFF0C\u6548\u598D\u4E11\u4E8E\u6BEB\u7AEF\uFF0C\u4FC4\u800C\u6B4C\u6B8B\u573A\u7F62\uFF0C\u598D\u4E11\u4F55\u5B58\uFF1F\u5F08\u8005\u4E89\u5148\u7ADE\u540E\uFF0C\u8F83\u96CC\u96C4\u4E8E\u6307\u4E0B\uFF0C\u4FC4\u800C\u5C40\u6563\u5B50\u6536\uFF0C\u96CC\u96C4\u5B89\u5728\uFF1F</p><p>\u70B9\u8BC4\uFF1A\u5FD9\u788C\u4E00\u751F\uFF0C\u5374\u4EAB\u53D7\u4E0D\u5230\u4EBA\u751F\u7684\u8212\u9002\u5FEB\u4E50\uFF0C\u4EBA\u751F\u65C5\u7A0B\u5C31\u8349\u8349\u7ED3\u675F\u4E86\uFF0C\u8FD9\u6837\u7684\u4EBA\u751F\u5B9E\u5728\u4E0D\u53EF\u53D6\u3002</p>',91),i=[d];function t(n,c){return a(),e("div",null,i)}var o=p(r,[["render",t],["__file","\u300A\u83DC\u6839\u8C2D\u300B\u8BFB\u4E66\u7B14\u8BB0.html.vue"]]);export{o as default};
