import{_ as p,o as e,c as a,a as r}from"./app.4524d535.js";const h={},t=r('<h1 id="\u300A\u91D1\u74F6\u6885\u300B\u8BFB\u4E66\u7B14\u8BB0" tabindex="-1"><a class="header-anchor" href="#\u300A\u91D1\u74F6\u6885\u300B\u8BFB\u4E66\u7B14\u8BB0" aria-hidden="true">#</a> \u300A\u91D1\u74F6\u6885\u300B\u8BFB\u4E66\u7B14\u8BB0</h1><h2 id="\u5173\u4E8E\u672C\u4E66" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E\u672C\u4E66" aria-hidden="true">#</a> \u5173\u4E8E\u672C\u4E66</h2><blockquote><p>\u4E66\u540D\uFF1A\u91D1\u74F6\u6885</p><p>\u4F5C\u8005\uFF1A\u5170\u9675\u7B11\u7B11\u751F(\u660E)</p><p>\u81EA\u6211\u8BC4\u5206\uFF1A\u2605\u2605\u2605\u2605\u2605</p><p>\u9605\u8BFB\u65F6\u95F4\uFF1A2020\u5E747\u6708</p></blockquote><h2 id="\u5185\u5BB9\u7B80\u4ECB" tabindex="-1"><a class="header-anchor" href="#\u5185\u5BB9\u7B80\u4ECB" aria-hidden="true">#</a> \u5185\u5BB9\u7B80\u4ECB</h2><p>\u91D1\u74F6\u6885\u662F\u4E00\u672C\u6DEB\u4E66\uFF0C\u5F53\u7136\u66F4\u662F\u4E00\u672C\u975E\u5E38\u4E16\u4FD7\u7684\u4E66\uFF0C\u91D1\u74F6\u6885\u5404\u6307\u6F58\u91D1\u83B2\u3001\u674E\u74F6\u513F\u3001\u5E9E\u6625\u6885\u4E09\u5973\uFF0C\u5F53\u7136\u4E3B\u89D2\u662F\u897F\u95E8\u5E86\u3002</p><h2 id="\u8BFB\u65F6\u968F\u60F3" tabindex="-1"><a class="header-anchor" href="#\u8BFB\u65F6\u968F\u60F3" aria-hidden="true">#</a> \u8BFB\u65F6\u968F\u60F3</h2><p>\u4E00</p><p>\u91D1\u74F6\u6885\u5F00\u7BC7\u5199\u897F\u95E8\u5E86\u7ED3\u4EA4\u5341\u4E2A\u670B\u53CB\uFF0C\u8FD8\u6709\u6B66\u677E\u6253\u5927\u866B\u7684\u6545\u4E8B\uFF0C\u8FD9\u662F\u540E\u6587\u7684\u94FA\u57AB\u3002\u91D1\u83B2\uFF0C\u6DEB\u5987\u4E5F\uFF1B\u5927\u90CE\uFF0C\u4E09\u5BF8\u4E01\u3002\u91D1\u83B2\u4E8C\u5341\u4E94\u5C81\uFF0C\u5E73\u65F6\u6027\u751F\u6D3B\u5E94\u8BE5\u4E0D\u600E\u4E48\u6837\uFF0C\u5F85\u6B66\u4E8C\u56DE\u6765\u7ACB\u9A6C\u64A9\u6C49\u5B50\uFF0C\u8C01\u60F3\u6B66\u4E8C\u662F\u6709\u5E95\u7EBF\u7684\u4EBA\uFF0C\u76F4\u63A5\u62D2\u7EDD\uFF0C\u4E0D\u7559\u60C5\u9762\uFF0C\u6DEB\u5987\u53CD\u5012\u6253\u4E00\u8019\uFF0C\u8BEC\u9677\u6B66\u4E8C\uFF0C\u8FD9\u91D1\u83B2\u5F00\u7BC7\u7ED9\u4EBA\u7684\u5370\u8C61\u5C31\u4E0D\u597D\u3002</p><p>\u6B66\u4E8C\u8D70\u4E86\uFF0C\u897F\u95E8\u5E86\u5076\u7136\u9047\u91D1\u83B2\uFF0C\u770B\u4E0A\u4E86\uFF0C\u9042\u5BFB\u738B\u5A46\u51FA\u8BA1\u3002\u8FD9\u738B\u5A46\u4E5F\u6DF1\u8C19\u4EBA\u5FC3\uFF0C\u7275\u7EBF\u642D\u6865\uFF0C\u4EE5\u505A\u8863\u670D\u7684\u540D\u4E49\u8BA9\u897F\u95E8\u5E86\u6CE1\u5230\u91D1\u83B2\uFF0C\u81EA\u5DF1\u4E5F\u635E\u5230\u4E86\u597D\u5904\u3002\u7D27\u63A5\u7740\u90D3\u54E5\u627E\u897F\u95E8\u5E86\u62FF\u94B1\u800D\uFF0C\u53D1\u73B0\u897F\u95E8\u5B98\u4EBA\u5077\u60C5\uFF0C\u53D7\u738B\u5A46\u6C14\u3002\u8FD9\u4E00\u53D7\u6C14\u81EA\u7136\u662F\u5BFB\u6C42\u62A5\u590D\uFF0C\u76F4\u627E\u6B66\u5927\uFF0C\u62FF\u4E86\u597D\u5904\u4E00\u8D77\u6349\u5978\u3002</p><p>\u8FD9\u6B66\u5927\u4E5F\u662F\u7A9D\u56CA\uFF0C\u6349\u5978\u53CD\u88AB\u8E39\uFF0C\u75C5\u5165\u818F\u8093\uFF0C\u53CD\u88AB\u738B\u5A46\u5B9A\u8BA1\u9E29\u6740\u3002\u8FD9\u6BB5\u5199\u7684\u51FA\u5947\u7684\u96BE\u53D7\uFF0C\u6B66\u5927\u592A\u81EA\u5351\u4E86\uFF0C\u597D\u4E0D\u5BB9\u6613\u627E\u4E00\u4E2A\u6F02\u4EAE\u5AB3\u5987\uFF0C\u88AB\u7EFF\u4E86\u8FD8\u4E3A\u8001\u5A46\u7740\u60F3\uFF0C\u6B7B\u5728\u5E8A\u4E0A\u3002\u8FD9\u4E00\u6BB5\u91D1\u83B2\u738B\u5A46\u6740\u4EBA\u5982\u6B64\u51B7\u9759\uFF0C\u897F\u95E8\u5E86\u4E70\u901A\u6775\u4F5C\u7792\u810F\uFF0C\u5973\u513F\u83BA\u513F\u4E0D\u6562\u542D\u58F0\uFF0C\u5916\u4EBA\u660E\u77E5\u4E0D\u662F\u75C5\u6B7B\u5374\u65E0\u4E00\u8BF4\u51FA\u771F\u76F8\uFF0C\u4E5F\u8BB8\u662F\u4E8B\u4E0D\u5173\u5DF1\uFF0C\u4E5F\u8BB8\u662F\u5BB3\u6015\u6743\u52BF\uFF0C\u81F3\u4E8E\u90D3\u54E5\uFF0C\u53EA\u662F\u4E3A\u4E86\u62FF\u94B1\uFF0C\u53EA\u662F\u4E3A\u4E86\u51FA\u53E3\u6C14\u7F62\u4E86\uFF0C\u6B66\u5927\u5C31\u8FD9\u4E48\u4E0D\u660E\u4E0D\u767D\u7684\u6B7B\u4E86\u3002</p><p>\u8BA9\u4EBA\u611F\u5230\u8352\u5510\u7684\u662F\u4FE9\u4EBA\u73B0\u5728\u6562\u5149\u660E\u6B63\u5927\u7684\u641E\u4E86\uFF0C\u5728\u6B66\u5927\u7684\u5893\u724C\u524D\u53CC\u98DE\u71D5\u3002</p><p>\u4E8C</p><p>\u6B66\u677E\u7EC8\u4E8E\u56DE\u6765\u4E86\uFF0C\u6653\u5F97\u4E86\u8FD9\u5077\u5978\u7684\u4E8B\u513F\uFF0C\u7ED3\u679C\u592A\u51B2\u52A8\uFF0C\u8BEF\u6253\u4E86\u674E\u7682\u96B6\uFF0C\u53D1\u914D\u5230\u5916\u5730\uFF0C\u91D1\u83B2\u4E5F\u7EC8\u4E8E\u8D5A\u5165\u897F\u95E8\u5E86\u5BB6\u3002\u8FD9\u897F\u95E8\u5E86\u8001\u5A46\u4E0D\u5C11\uFF0C\u6B63\u59BB\u5434\u6708\u5A18\uFF0C\u5269\u4E0B\u7684\u59BE\u4F9D\u6B21\u4E3A\u674E\u5A07\u513F\u3001\u5B5F\u7389\u697C\u3001\u5B59\u96EA\u5A25\u3001\u6F58\u91D1\u83B2\uFF0C\u5176\u4E2D\u674E\u5A07\u513F\u548C\u5B59\u96EA\u5A25\u4F4E\u4F4D\u8F83\u4F4E\uFF0C\u674E\u5A07\u513F\u662F\u5993\u5973\u51FA\u8EAB\uFF0C\u5B59\u96EA\u5A25\u662F\u9648\u6C0F\u7684\u966A\u5E8A\u4E2B\u5934\u3002\u91D1\u83B2\u4E00\u6765\u5C31\u5F97\u7F6A\u4E86\u5979\u4FE9\uFF0C\u8FD9\u5B59\u96EA\u5A25\u867D\u4E0E\u5176\u4ED6\u4EBA\u4E00\u6837\u90FD\u662F\u59BE\uFF0C\u4F46\u5979\u662F\u4E3B\u7BA1\u996E\u98DF\u7684\uFF0C\u5927\u96C6\u4F1A\u4E5F\u6CA1\u673A\u4F1A\u53BB\uFF0C\u6CA1\u6709\u91D1\u83B2\u90A3\u4E48\u5706\u6ED1\u4E16\u6545\uFF0C\u6CA1\u6709\u7389\u697C\u90A3\u6837\u6C89\u7A33\u6301\u5BB6\uFF0C\u5FC3\u5982\u7409\u7483\uFF0C\u4E0D\u61C2\u4EBA\u60C5\uFF0C\u7ADF\u7136\u4E0E\u91D1\u83B2\u548C\u6625\u6885\u5F53\u9762\u6495\u7834\u8138\uFF0C\u88AB\u6B3A\u8D1F\u662F\u5FC5\u7136\u7684\u3002</p><p>\u8FD9\u897F\u95E8\u5E86\u662F\u4E2A\u82B1\u5FC3\u7684\u4EBA\uFF0C\u91D1\u83B2\u521A\u5165\u95E8\u53C8\u53BB\u627E\u674E\u6842\u59D0\uFF0C\u8FD9\u91D1\u83B2\u4E5F\u662F\u90C1\u95F7\uFF0C\u5F00\u59CB\u8DDF\u5C0F\u53AE\u641E\u4E00\u8D77\uFF0C\u4E0D\u60F3\u88AB\u8BB0\u4EC7\u7684\u674E\u5A07\u513F\u548C\u5B59\u96EA\u5A25\u4E3E\u62A5\uFF0C\u53D7\u897F\u95E8\u5E86\u4E00\u987F\u6253\uFF0C\u6C42\u7239\u6C42\u5A18\u7684\u7CCA\u5F04\u8FC7\u53BB\u4E86\u3002</p><p>\u518D\u8BF4\u674E\u74F6\u513F\uFF0C\u7684\u786E\u82B1\u679D\u62DB\u5C55\uFF0C\u975E\u5E38\u6F02\u4EAE\u3002\u8FD9\u5C0F\u5A46\u5A18\u4E08\u592B\u82B1\u5B50\u865A\u4E0E\u897F\u95E8\u5E86\u662F\u94C1\u54E5\u4EEC\uFF0C\u7ED3\u679C\u897F\u95E8\u5E86\u8BA9\u5176\u4ED6\u4EBA\u628A\u82B1\u5B50\u865A\u652F\u5F00\uFF0C\u548C\u674E\u74F6\u513F\u641E\u4E00\u8D77\u4E86\uFF0C\u5899\u5934\u5BC6\u7EA6\uFF0C\u8FD9\u5144\u5F1F\u8499\u5728\u9F13\u91CC\u3002\u53EF\u60DC\u554A\uFF0C\u7978\u4E0D\u5355\u884C\uFF0C\u82B1\u5B50\u865A\u88AB\u4ED6\u51E0\u4E2A\u54E5\u54E5\u5F1F\u5F1F\u544A\u4E86\uFF0C\u5403\u4E0A\u5B98\u53F8\uFF0C\u53EA\u4E3A\u4E89\u593A\u4E0A\u4E00\u4EE3\u7559\u4E0B\u7684\u8D22\u4EA7\uFF0C\u800C\u674E\u74F6\u513F\u6B64\u65F6\u5012\u6253\u4E00\u8019\uFF0C\u628A\u5BB6\u4EA7\u5F80\u5916\u9001\uFF0C\u4E0D\u4F46\u5982\u6B64\uFF0C\u8FD8\u4EE5\u4E00\u79CD\u5C16\u9178\u523B\u8584\u7684\u52B2\u6316\u82E6\u82B1\u5B50\u865A\u3002\u8FD9\u82B1\u5B50\u865A\u5185\u5916\u53D7\u654C\uFF0C\u751A\u662F\u51C4\u60E8\uFF0C\u8FDE\u540C\u623F\u5B50\u548C\u8001\u5A46\u5356\u7ED9\u4E86\u897F\u95E8\u5E86\uFF0C\u51C6\u786E\u7684\u8BF4\uFF0C\u674E\u74F6\u513F\u8FD8\u9001\u4E86\u897F\u95E8\u5E86\u4E0D\u5C11\u5947\u73CD\u5F02\u5B9D\uFF0C\u6700\u7EC8\u843D\u4E0B\u75C5\u6765\u6492\u624B\u4EBA\u5BF0\u3002</p><p>\u8FD9\u897F\u95E8\u5E86\u81EA\u5F97\u4E86\u674E\u74F6\u513F\uFF0C\u5982\u5F97\u4E86\u5B9D\u8D1D\u4E00\u822C\uFF0C\u671D\u601D\u66AE\u60F3\u53BB\u5979\u90A3\u513F\uFF0C\u90A3\u6842\u59D0\u3001\u91D1\u83B2\u65E9\u5FD8\u4E86\uFF0C\u8FD9\u674E\u74F6\u513F\u5F85\u4EBA\u63A5\u7269\u751A\u662F\u5927\u65B9\uFF0C\u8D35\u91CD\u7684\u91D1\u7C2A\u76F4\u63A5\u9001\u4E86\u56DB\u628A\uFF0C\u8FC7\u8282\u628A\u897F\u95E8\u5BB6\u59D0\u59D0\u90FD\u8BF7\u8FC7\u4E86\uFF0C\u9664\u4E86\u4F4E\u4F4D\u5351\u5FAE\u7684\u5B59\u96EA\u5A25\u3002\u5230\u4E86\u665A\u4E0A\uFF0C\u4FE9\u4EBA\u9E33\u9E2F\u620F\u6C34\u3001\u6FC0\u60C5\u56DB\u5C04\uFF0C\u8C08\u8D77\u6765\u7ED9\u674E\u74F6\u513F\u76D6\u623F\u5B50\u7684\u4E8B\u60C5\uFF0C\u539F\u662F\u8FD9\u674E\u74F6\u513F\u592A\u60F3\u8FC7\u95E8\u3002\u5230\u8FD9\u91CC\uFF0C\u6211\u59CB\u7EC8\u641E\u4E0D\u61C2\u674E\u74F6\u513F\u770B\u4E2D\u897F\u95E8\u5E86\u4EC0\u4E48\u4E86\uFF0C\u9001\u94B1\u53C8\u9001\u4EBA\uFF0C\u4F46\u6211\u77E5\u9053\u81F3\u5C11\u897F\u95E8\u5E86\u770B\u4E2D\u4E86\u5979\u7684\u7F8E\u8C8C\u3002</p><p>\u4E09</p><p>\u8F97\u8F6C\u591A\u6B21\u7684\u674E\u74F6\u513F\u7EC8\u4E8E\u8D5A\u5165\u897F\u95E8\u5E86\u5BB6\uFF0C\u4EBA\u79F0\u516D\u5A18\uFF0C\u51FA\u624B\u5927\u65B9\uFF0C\u80F8\u65E0\u57CE\u5E9C\uFF0C\u8FD9\u662F\u5979\u5C55\u793A\u51FA\u6765\u7684\u7279\u70B9\uFF0C\u602A\u8BA8\u4F17\u4EBA\u559C\u6B22\u7684\u3002</p><p>\u897F\u95E8\u5E86\u60D1\u8C17\uFF0C\u4E0E\u5434\u6708\u5A18\u6709\u9699\uFF0C\u897F\u95E8\u5E86\u4E0D\u77E5\u5185\u5916\uFF0C\u4F46\u5434\u6708\u5A18\u8FD8\u662F\u5E0C\u671B\u5BB6\u91CC\u548C\u7766\u7684\uFF0C\u5F00\u59CB\u4E3A\u4E08\u592B\u7948\u7977\uFF0C\u5E0C\u671B\u4E0D\u8981\u88AB\u5916\u9762\u7684\u5996\u5973\u86CA\u60D1\uFF0C\u8C01\u77E5\u8FD9\u8BDD\u88AB\u897F\u95E8\u5E86\u542C\u5230\uFF0C\u5176\u6DF1\u611F\u60ED\u6127\uFF0C\u4E3B\u52A8\u548C\u597D\uFF0C\u4ECE\u6B64\u540E\u897F\u95E8\u5E86\u5F88\u4FE1\u4EFB\u5434\u6708\u5A18\u3002\u5BB6\u4E2D\u4E4B\u4E8B\u5747\u662F\u6708\u5A18\u6253\u7406\uFF0C\u8FD9\u6B63\u662F\u8001\u5A46\u548C\u5C0F\u59BE\u7684\u4E0D\u540C\u5427\uFF0C\u6795\u8FB9\u592B\u59BB\u56FE\u4E2A\u5B89\u7A33\u4E0D\u8981\u94B1\uFF0C\u5916\u9762\u7684\u5973\u4EBA\u518D\u6F02\u4EAE\u65E0\u975E\u662F\u770B\u4E0A\u4F60\u7684\u94B1\u8D22\u3002</p><p>\u4E14\u8BF4\u5B8B\u60E0\u83B2\u51FA\u573A\u5C31\u6709\u70B9\u53EF\u601C\u76F8\uFF0C\u5979\u662F\u6765\u65FA\u513F\u5AB3\u5987\uFF0C\u56E0\u51E0\u5206\u59FF\u8272\u88AB\u897F\u95E8\u5E86\u7528\u51E0\u6761\u4E1D\u7EF8\u52FE\u642D\u4E0A\u4E86\uFF0C\u91D1\u83B2\u7ED9\u4ED6\u4FE9\u63D0\u4F9B\u4E86\u5077\u5978\u7684\u573A\u5730\uFF0C\u7389\u8427\u7ED9\u671B\u98CE\u3002\u8C01\u77E5\u8FD9\u8D27\u4E0D\u8BC6\u597D\u6B79\uFF0C\u6CA1\u770B\u6E05\u81EA\u5DF1\u7684\u8EAB\u4EFD\u5730\u4F4D\uFF0C\u53D7\u4E3B\u5B50\u5BA0\u4E86\u5C31\u5F00\u59CB\u5F97\u745F\u70AB\u8000\uFF0C\u660E\u660E\u662F\u91D1\u83B2\u7ED9\u63D0\u4F9B\u7684\u4E0A\u4F4D\u7684\u673A\u4F1A\u4F60\u8FD8\u53CD\u8FC7\u6765\u80CC\u5730\u91CC\u9A82\u4EBA\u5BB6\u660E\u91CC\u8BBD\u523A\u4EBA\u5BB6\u811A\u5927\uFF0C\u5389\u5BB3\u7684\u5F00\u59CB\u6307\u793A\u5176\u4ED6\u5974\u4EC6\u7092\u8336\uFF0C\u4E00\u5974\u624D\u5AB3\u5987\u5F00\u59CB\u6253\u8DA3\u59BB\u59BE\u9636\u5C42\uFF0C\u88AB\u5B5F\u7389\u697C\u76F4\u63A5\u603C\u56DE\u53BB\u3002\u771F\u662F\u5FC3\u6BD4\u5929\u9AD8\uFF0C\u8EAB\u4E3A\u4E0B\u8D31\uFF0C\u8BA9\u4F17\u4EBA\u8C01\u4E0D\u5FC3\u751F\u53CD\u611F\uFF1F</p><p>\u6CA1\u60F3\u5230\u8FD9\u5B59\u96EA\u5A25\u548C\u6765\u65FA\u513F\u7ADF\u7136\u6709\u4E00\u817F\uFF0C\u8FD9\u4E5F\u662F\u4E0D\u5B89\u5206\u7684\u4E3B\uFF0C\u8BD5\u60F3\u5979\u7684\u52A8\u673A\u662F\u4EC0\u4E48\uFF1F\u662F\u771F\u7684\u767D\u7ED9\u6765\u65FA\u513F\u4E48\uFF1F\u663E\u7136\u4E0D\u662F\uFF0C\u6211\u731C\u60F3\u5979\u662F\u60F3\u901A\u8FC7\u897F\u95E8\u5E86\u8EAB\u8FB9\u7684\u4EB2\u8FD1\u4EC6\u4EBA\u6765\u62C9\u8FD1\u4E0E\u897F\u95E8\u5E86\u7684\u5173\u7CFB\uFF0C\u8FDB\u800C\u8BBE\u6CD5\u63D0\u9AD8\u5728\u5BB6\u4E2D\u7684\u5351\u8D31\u5730\u4F4D\u3002\u4E0D\u6599\u8FD9\u4E8B\u513F\u88AB\u6345\u51FA\u6765\u4E86\uFF0C\u88AB\u6253\u4E5F\u662F\u6D3B\u8BE5\u3002</p><p>\u4E14\u8BF4\u6765\u65FA\u513F\u5F52\u6765\uFF0C\u88AB\u5B59\u96EA\u5A25\u544A\u77E5\u8001\u5A46\u5B8B\u60E0\u83B2\u8DDF\u4E3B\u5B50\u897F\u95E8\u5E86\u6709\u67D3\uFF0C\u800C\u4E14\u662F\u6F58\u91D1\u83B2\u642D\u7684\u7EBF\uFF0C\u5927\u6012\uFF0C\u626C\u8A00\u8981\u6740\u897F\u95E8\u5E86\uFF0C\u628A\u91D1\u83B2\u9A82\u7684\u72D7\u8840\u55B7\u5934\uFF0C\u751A\u81F3\u4E4B\u524D\u6740\u6B7B\u6B66\u5927\u4E70\u901A\u5B98\u5E9C\u7684\u4E8B\u513F\u90FD\u6296\u51FA\u6765\uFF0C\u91D1\u83B2\u542C\u4E86\u5982\u4F55\u4E0D\u6C14\uFF1F\u897F\u95E8\u5E86\u542C\u4E86\u5982\u4F55\u4E0D\u6015\uFF1F\u717D\u98CE\u70B9\u706B\u53EF\u4EE5\u91D1\u83B2\u6700\u64C5\u957F\u7684\u5475\uFF0C\u897F\u95E8\u5E86\u7ECF\u91D1\u83B2\u70B9\u62E8\u7ACB\u523B\u7ED9\u6765\u65FA\u513F\u4E0B\u5957\uFF0C\u4EE5\u8C0B\u5BB3\u4E3B\u5B50\u4E3A\u7F6A\u8BC1\u60F3\u5C06\u4ED6\u7F6E\u4E8E\u6B7B\u5730\uFF0C\u8FD9\u6837\u5B8B\u60E0\u83B2\u5C31\u53EF\u4EE5\u505A\u4E03\u623F\u4E86\uFF0C\u6CA1\u60F3\u5230\u8FD9\u5B8B\u60E0\u83B2\u5982\u6B64\u7275\u6302\u6765\u65FA\u513F\uFF0C\u82E6\u82E6\u8DEA\u6C42\u897F\u95E8\u5E86\u653E\u4ED6\u4E00\u9A6C\u3002\u897F\u95E8\u5E86\u6709\u4E9B\u5FC3\u5BD2\uFF0C\u539F\u6765\u53EA\u5F97\u4F60\u8EAB\u672A\u5F97\u4F60\u5FC3\uFF0C\u518D\u52A0\u4E0A\u91D1\u83B2\u4E8C\u6B21\u717D\u98CE\u70B9\u706B\uFF0C\u897F\u95E8\u5E86\u76F4\u63A5\u628A\u6765\u65FA\u513F\u53D1\u914D\u5230\u5F90\u5DDE\uFF0C\u5B8B\u60E0\u83B2\u7B2C\u4E00\u6B21\u81EA\u7F22\uFF0C\u8FD8\u597D\u88AB\u9694\u58C1\u4E00\u4E2A\u4E0D\u77E5\u540D\u7684\u5974\u4EC6\u6551\u4E0B\u3002\u53EF\u91D1\u83B2\u662F\u4E0D\u5584\u7F62\u7518\u4F11\u7684\uFF0C\u975E\u8981\u6574\u6B7B\u4F60\uFF0C\u53C8\u53BB\u5230\u5B59\u96EA\u5A25\u90A3\u513F\u717D\u9634\u706B\uFF0C\u7EC8\u4E8E\u5728\u4E0E\u5B59\u96EA\u5A25\u7684\u5435\u95F9\u540E\u7EDD\u671B\u542B\u6CEA\u81EA\u7F22\u3002</p><p>\u5B8B\u60E0\u83B2\u6B7B\u540E\uFF0C\u8FD8\u662F\u90A3\u5957\u719F\u6089\u7684\u4E70\u529E\u5B98\u5E9C\u505A\u5047\u6848\u7B49\uFF0C\u7236\u4EB2\u5B8B\u4EC1\u8DDF\u7740\u4E00\u8D77\u966A\u846C\u3002\u65E0\u4EBA\u5E74\u6302\u5979\uFF0C\u65E0\u4EBA\u601C\u60AF\u5979\uFF0C\u65E0\u4EBA\u4E0D\u89C9\u5F97\u8FD9\u4E0D\u6B63\u5E38\uFF0C\u751A\u81F3\u63D0\u5230\u5979\u8FD8\u8981\u9A82\u51E0\u53E5\uFF0C\u7D27\u63A5\u7740\u5C31\u662F\u6700\u7ECF\u5178\u7684\u8461\u8404\u67B6\u4EA4\u6B22\uFF0C\u5C3A\u5EA6\u4E4B\u5927\uFF0C\u7BC7\u5E45\u4E4B\u957F\uFF0C\u8BA9\u4EBA\u4F3C\u4E4E\u90FD\u5FD8\u8BB0\u5B8B\u60E0\u83B2\u4E86\uFF0C\u8FD9\u4E5F\u8BB8\u662F\u4F5C\u8005\u6545\u610F\u5B89\u6392\u7684\u5427\u3002</p><p>\u5B8B\u60E0\u83B2\u5728\u897F\u95E8\u5E86\u90A3\u513F\u4E0D\u8FC7\u662F\u4E2A\u6682\u65F6\u5F97\u5BA0\u7684\u5C24\u7269\u7F62\u4E86\uFF0C\u6709\u4F60\uFF0C\u5728\u4F60\u8EAB\u4E0A\u5C3D\u60C5\u7EB5\u6B32\uFF1B\u6CA1\u6709\u4F60\uFF0C\u4E5F\u53EF\u4EE5\u627E\u5176\u4ED6\u5973\u4EBA\u5BFB\u6B22\u4F5C\u4E50\u3002\u540C\u662F\u901A\u5978\uFF0C\u91D1\u83B2\u5BB3\u6B7B\u6B66\u5927\u90CE\uFF0C\u60E0\u83B2\u53AE\u5B88\u6765\u65FA\u513F\u3002\u6211\u5BF9\u60E0\u83B2\u53CD\u800C\u6709\u4E00\u4E1D\u601C\u60AF\u4E4B\u5FC3\u5427\uFF0C\u5979\u5E76\u4E0D\u662F\u574F\u5230\u9AA8\u5B50\u91CC\uFF0C\u5B8B\u60E0\u83B2\u662F\u6709\u865A\u8363\u5FC3\uFF0C\u53EF\u5979\u6CA1\u6709\u5BB3\u4EBA\u4E4B\u5FC3\uFF0C\u60E8\u54C9\uFF01\u4EBA\u5FC3\u554A\uFF0C\u90FD\u662F\u64C5\u5AC9\u5992\u7684\uFF0C\u4E0D\u77E5\u5929\u9AD8\u5730\u539A\uFF0C\u6CA1\u6709\u6446\u6B63\u81EA\u5DF1\u7684\u4F4D\u7F6E\uFF0C\u9AD8\u8C03\u5FC5\u53D7\u6697\u7BAD\uFF0C\u5230\u6B7B\u4E5F\u6CA1\u6709\u4E00\u4E2A\u8DDF\u81EA\u5DF1\u7AD9\u5728\u540C\u4E00\u6218\u7EBF\u4E0A\u7684\u4EBA\u3002</p>',24),d=[t];function c(_,i){return e(),a("div",null,d)}var o=p(h,[["render",c],["__file","\u300A\u91D1\u74F6\u6885\u300B\u8BFB\u4E66\u7B14\u8BB0.html.vue"]]);export{o as default};
