import{_ as t,r as n,o,c as e,b as p,d as s,a,e as _}from"./app.4524d535.js";const c={},g=a('<h1 id="\u6211\u56FD\u666E\u901A\u7537\u5982\u4F55\u5728\u7537\u5973\u5173\u7CFB\u4E2D\u5B9E\u73B0-\u5229\u76CA-\u540D\u58F0-\u7684\u6700\u5927\u5316-\u2014\u2014\u5F53\u4ECA\u4E2D\u56FD\u4EBA\u6027\u884C\u4E3A\u7684-\u6F5C-\u89C4\u5219" tabindex="-1"><a class="header-anchor" href="#\u6211\u56FD\u666E\u901A\u7537\u5982\u4F55\u5728\u7537\u5973\u5173\u7CFB\u4E2D\u5B9E\u73B0-\u5229\u76CA-\u540D\u58F0-\u7684\u6700\u5927\u5316-\u2014\u2014\u5F53\u4ECA\u4E2D\u56FD\u4EBA\u6027\u884C\u4E3A\u7684-\u6F5C-\u89C4\u5219" aria-hidden="true">#</a> \u6211\u56FD\u666E\u901A\u7537\u5982\u4F55\u5728\u7537\u5973\u5173\u7CFB\u4E2D\u5B9E\u73B0\uFF08\u5229\u76CA+\u540D\u58F0\uFF09\u7684\u6700\u5927\u5316\uFF1F\u2014\u2014\u5F53\u4ECA\u4E2D\u56FD\u4EBA\u6027\u884C\u4E3A\u7684\uFF08\u6F5C\uFF09\u89C4\u5219</h1><p>\u6211\u6700\u8FD1\u4E24\u5929\u5BF9\u5927\u5BB6\u8BF4\u4E00\u5B9A\u8981\u6709\u72EC\u7ACB\u81EA\u4E3B\u7684\u601D\u8003\uFF0C\u4E0D\u8981\u88AB\u522B\u4EBA\u7275\u7740\u9F3B\u5B50\u8D70\u3002<strong>\u4F46\u662F\uFF0C\u6211\u4EEC\u5728\u8FD9\u4E2A\u4E16\u754C\u751F\u5B58\uFF0C\u4E00\u5B9A\u8981\u4E86\u89E3\u4E3B\u6D41\u8206\u8BBA\uFF08\u4F26\u7406\u9053\u5FB7\uFF09\u4E0E\u4E3B\u6D41\u884C\u4E3A\u662F\u4EC0\u4E48\u3002\u4E0D\u53EF\u4EE5\u56E0\u4E3A\u81EA\u5DF1\u72EC\u7ACB\u601D\u8003\u7684\u7ED3\u8BBA\u662FA\uFF0C\u5C31\u5929\u771F\u5730\u89C9\u5F97\u4E3B\u6D41\u4E5F\u662F\u8D5E\u540CA\uFF0C\u8FD9\u662F\u4E24\u7801\u4E8B\u3002</strong></p><p>\u90A3\u4E48\uFF0C\u6211\u4EEC\u5982\u4F55\u4E86\u89E3\u4E3B\u6D41\u601D\u60F3\u6216\u8005\u4E3B\u6D41\u7684\u884C\u4E3A\u5462\uFF1F\u6700\u4E25\u8C28\u7684\u529E\u6CD5\u5F53\u7136\u662F\u627E\u6B63\u89C4\u8C03\u67E5\u7684\u7ED3\u679C\uFF0C\u8FD9\u4E2A\u529E\u6CD5\u5927\u5BB6\u90FD\u77E5\u9053\u3002<strong>\u8FD8\u6709\u4E00\u4E2A\u529E\u6CD5\uFF0C\u5C31\u662F\u770B\u5728\u4E00\u4E2A\u573A\u5408\u5927\u5BB6\u80FD\u591F\u5766\u7136\u63D0\u53CA\u54EA\u4E00\u7C7B\u4E8B\u60C5\u3001\u9ED8\u8BA4\u4E0D\u8981\u63D0\u54EA\u4E00\u7C7B\u4E8B\u60C5\uFF0C\u8FD9\u662F\u5728\u63A5\u89E6\u4E0D\u5230\u6B63\u89C4\u7EDF\u8BA1\u8C03\u67E5\u65F6\u5224\u65AD\u793E\u4F1A\u4E3B\u6D41\u8D70\u5411\u7684\u5229\u5668\uFF0C\u4E5F\u662F\u6211\u6D1E\u5BDF\u793E\u4F1A\u7684\u4E00\u79CD\u65B9\u6CD5\u3002</strong></p><p>\u4E3E\u4E2A\u4F8B\u5B50\uFF0C\u7532\u6307\u63A7\u4E59\u4E0D\u73A9\u547D\u52AA\u529B\u7ED9\u4ED6\u7684\u5DF2\u6210\u5E74\u5B50\u5973\u8D5A\u94B1\uFF0C\u56F4\u89C2\u8005\u4E0D\u89C9\u5F97\u7532\u7684\u6001\u5EA6\u6709\u5565\u4E0D\u5BF9\u3002\u90A3\u4E48\uFF0C\u6211\u53EF\u4EE5\u5224\u65AD\u4E2D\u56FD\u793E\u4F1A\uFF08\u81F3\u5C11\u662F\u8FD9\u4E2A\u5730\u65B9\u7684\u793E\u4F1A\uFF09\u7684\u4E3B\u6D41\u8206\u8BBA\u662F\u2014\u2014\u5543\u8001\u5F88\u6B63\u5E38\u3002</p><p>\u518D\u4E3E\u4E2A\u4F8B\u5B50\uFF0C\u6211\u89C1\u8FC7\u67D0\u5A5A\u4ECB\u6240\u5916\u5899\u7684\u5F81\u5A5A\u542F\u4E8B\u90FD\u662F\u683C\u5F0F\u5316\u7684\uFF0C\u7537\u5F81\u5973\u5FC5\u987B\u5199\u6536\u5165\u3001\u8F66\u623F\uFF0C\u4E0D\u5199\u4E0D\u884C\u3002\u90A3\u4E48\uFF0C\u6211\u53EF\u4EE5\u5224\u65AD\u4E2D\u56FD\u793E\u4F1A\uFF08\u81F3\u5C11\u662F\u8FD9\u4E2A\u5730\u65B9\u7684\u793E\u4F1A\uFF09\u7684\u4E3B\u6D41\u8206\u8BBA\u662F\u2014\u2014\u7537\u65B9\u7ED3\u5A5A\u5C31\u5FC5\u987B\u63D0\u4F9B\u8FD9\u4E9B\u4E1C\u897F\u3002</p><hr><p>\u6211\u4EEC\u5176\u5B9E\u4E0D\u9700\u8981\u53BB\u505A\u793E\u4F1A\u8C03\u67E5\uFF0C\u6211\u4EEC\u53EA\u9700\u8981\u770B\u4EBA\u4EEC\u5728\u975E\u79C1\u5BC6\u72B6\u6001\u4E0B\u5230\u5E95\u5728\u5927\u5927\u65B9\u65B9\uFF08\u4ECE\u6B63\u9762\u610F\u4E49\uFF09\u8C08\u4EC0\u4E48\uFF0C\u62B5\u5236\u4EC0\u4E48\uFF0C\u5C31\u53EF\u4EE5\u95F4\u63A5\u63A8\u65AD\u793E\u4F1A\u7684\u4E3B\u6D41\u3002</p><p><strong>\u9996\u5148\uFF0C\u5A5A\u59FB\u662F\u88AB\u5927\u5BB6\u8BA4\u53EF\u7684\uFF0C\u8FD9\u662F\u5E9F\u8BDD\uFF0C\u5C31\u4E0D\u591A\u8BF4\u4E86\u3002</strong></p><p><strong>\u7136\u540E\uFF0C\u5BF9\u4E8E\u5AD6\u5993\u3001\u5A5A\u5916\u60C5\u7684\u62B5\u5236\u76F8\u5F53\u6FC0\u70C8\u3002\uFF08\u6211\u8BA4\u4E3A\u201C\u966A\u5E8A\u4FDD\u59C6\u201D\u66F4\u63A5\u8FD1\u5305\u517B\u60C5\u5987\u800C\u4E0D\u662F\u5AD6\u5993\uFF09</strong></p><p>**\u5BF9\u4E8E\u201C\u4EE5\u604B\u7231\u4E3A\u540D\u7684\u6027\u751F\u6D3B\u201C\u662F\u5927\u5927\u65B9\u65B9\u7684\u3002**\u6839\u672C\u5C31\u4E0D\u9700\u8981\u53BB\u62FF\u793E\u4F1A\u8C03\u67E5\u6570\u636E\uFF0C\u5C31\u53EF\u4EE5\u95F4\u63A5\u63A8\u65AD\u604B\u7231\u4E2D\u7684\u6027\u751F\u6D3B\u65E9\u57FA\u672C\u4E0A\u201C\u53BB\u6C61\u540D\u5316\u4E86\u201D\u3002</p><p>\u5F53\u4F60\u6562\u4E8E\u5728\u77E5\u4E4E\u63D0\u4E3B\u6D41\u4E0D\u8BA4\u53EF\u7684\u6027\u884C\u4E3A\u7684\u65F6\u5019\uFF0C\u4F60\u653E\u5FC3\uFF0C\u4E00\u5806\u4EBA\u9A82\uFF1B\u5982\u679C\u6709\u4EBA\u5728\u77E5\u4E4E\u63D0\u4E00\u79CD\u6027\u884C\u4E3A\u7684\u6A21\u5F0F\u800C\u57FA\u672C\u4E0D\u88AB\u4E00\u5806\u4EBA\u51B2\u8FDB\u53BB\u8C34\u8D23\u7684\u65F6\u5019\uFF0C\u4F60\u53EF\u4EE5\u5224\u65AD\u8FD9\u79CD\u6A21\u5F0F\u7B26\u5408\u793E\u4F1A\u4E3B\u6D41\u3002\u6240\u4EE5\uFF0C\u5F53\u6211\u770B\u5230\u201C\u60DF\u660E\u5FC3\u201D\u529D\u4EBA\u4E0D\u8981\u6709\u5A5A\u524D\u6027\u884C\u4E3A\uFF0C\u6211\u89C9\u5F97\u771F\u8FC2\u8150\u3002</p><hr><p>\u6211\u518D\u4E3E\u4E00\u4E2A\u4F8B\u5B50\uFF0C**\u7537\u65B9\u4E0A\u95E8\u63D0\u4EB2\u7684\u65F6\u5019\u5973\u65B9\u6216\u5176\u7236\u6BCD\u63D0\u5230\u5F69\u793C\u3001\u8F66\u623F\uFF0C\u662F\u5F88\u5766\u7136\u7684\u3002\u4F46\u662F\u7537\u65B9\u501F\u201C\u5973\u65B9\u8DDF\u6211\u604B\u7231\u7684\u65F6\u5019\u5DF2\u7ECF\u4E0D\u662F\u5904\u5973\u201D\u6765\u4F01\u56FE\u538B\u4EF7\uFF0C\u662F\u8D8A\u6765\u8D8A\u7F55\u89C1\u4E86\u3002**\u5982\u679C\u4E00\u4E2A\u7537\u7684\u786C\u8981\u62FF\u8FD9\u4E2A\u6765\u538B\u4EF7\uFF0C\u793E\u4F1A\u5927\u4F17\u53CD\u800C\u89C9\u5F97\u8FD9\u4E2A\u7537\u6027\u201C\u4E0D\u4E0A\u9053\u201D\u3001\u201C\u4E0D\u8BC6\u8DA3\u201D\u3002\u8FD9\u5C31\u53EF\u4EE5\u95F4\u63A5\u63A8\u65AD\u51FA\uFF0C\u4E2D\u56FD\u7684\u4E3B\u6D41\u4F9D\u7136\u8BA4\u4E3A\u7537\u65B9\u7684\u7ECF\u6D4E\u8D23\u4EFB\u8FD8\u5728\uFF0C\u4F46\u662F\u5973\u65B9\u5DF2\u7ECF\u4E0D\u9700\u8981\u6709\u8D1E\u6D01\u8D23\u4EFB\u4E86\u3002</p><p><strong>\u5728\u540C\u4E00\u4E2A\u573A\u5408\uFF08\u5982\u5F81\u5A5A\u542F\u4E8B\u3001\u63D0\u4EB2\uFF09\uFF0C\u80FD\u591F\u5927\u5927\u65B9\u65B9\u88AB\u9891\u7E41\u63D0\u5230\u7684\u4E1C\u897F\uFF0C\u5C31\u662F\u4E3B\u6D41\uFF1B\u4F60\u4E00\u63D0\u5927\u5BB6\u5C31\u89C9\u5F97\u83AB\u540D\u5176\u5999\u751A\u81F3\u5C34\u5C2C\u7684\u4E1C\u897F\uFF0C\u5C31\u662F\u4E3B\u6D41\u629B\u5F03\u7684\u4E1C\u897F\u3002\u4E0D\u9700\u8981\u518D\u505A\u793E\u4F1A\u8C03\u67E5\u4E86\u3002</strong></p><hr><p>\u90A3\u4E48\uFF0C\u5927\u5BB6\u8C08\u604B\u7231\u7684\u65F6\u5019\u662F\u5426\u6BCF\u4E00\u6B21\u90FD\u7279\u771F\u5FC3\u5462\uFF1F\u4E91\u5357\u5927\u5B66\u6768\u5357\u4E3D\u6559\u6388\u7684\u8BBA\u6587\u300A\u4ECE\u5927\u5B66\u751F\u5A5A\u604B\u89C2\u73B0\u72B6\uFF0C\u770B\u9AD8\u6821\u5A5A\u604B\u89C2\u6559\u80B2\u2014\u2014\u4EE5\u5BF9\u4E91\u5357\u5927\u5B66\u751F\u5A5A\u604B\u89C2\u8C03\u67E5\u4E0E\u5206\u6790\u4E3A\u4F8B\u300B\u4E2D\u63D0\u5230\uFF0C\u4EC5\u670910.69%\u7684\u5B66\u751F\u62B1\u7740\u5EFA\u7ACB\u5BB6\u5EAD\u7684\u60F3\u6CD5\u8D70\u5165\u6821\u56ED\u604B\u60C5\u3002</p><p>\u5C31\u5728\u4ECA\u5929\uFF0C\u8FD8\u6709\u4EBA\u7ED9\u6211\u53D1\u79C1\u4FE1\u8BF4\u4ED6\u4EEC\u5B66\u6821\u7814\u7A76\u751F\u666E\u904D\u5728\u77E5\u9053\u6BD5\u4E1A\u5206\u624B\u51E0\u7387\u5F88\u5927\u7684\u524D\u63D0\u4E0B\u604B\u7231\uFF0C\u4ED6\u89C9\u5F97\u7B97\u201C\u77ED\u62E9\u201D\u3002</p><p>\u4E2D\u56FD\u4EBA\u65E9\u5C31\u9ED8\u8BA4\u7231\u5F97\u6BD4\u8F83\u6D45\u4E5F\u53EF\u4EE5\u53D1\u52A8\u604B\u7231\uFF08\u542B\u4E0A\u5E8A\uFF09\uFF0C\u6240\u8C13\u201C\u9ED8\u8BA4\u201D\u5C31\u662F\u4E0D\u516C\u5F00\u5BA3\u626C\u3001\u4E5F\u4E0D\u8C34\u8D23\u3002</p><hr><p>\u4E2D\u56FD\u793E\u4F1A\u7684\u6027\u9053\u5FB7\u7684\u8206\u8BBA\u53EF\u4EE5\u5F52\u7EB3\u4E3A\u4E09\u70B9\uFF1A</p><p>\u4E00\u3001<strong>\u975E\u5E38\u9119\u89C6</strong>\u5DF2\u5A5A\u4EBA\u58EB\u7684\u975E\u5A5A\u6027\u884C\u4E3A</p><p>\u4E8C\u3001<strong>\u5F88\u9119\u89C6</strong>\u672A\u5A5A\u3001\u79BB\u5F02\u3001\u4E27\u5076\u4EBA\u58EB\u7684\u5AD6\u5993\u3001\u7EA6\u70AE</p><p>\u4E09\u3001\u627F\u8BA4\u4EE5\u604B\u7231\u4E3A\u540D\u4E49\u7684\u6027\u884C\u4E3A\u5408\u7406\u3002\u5373\u4FBF\u7231\u5F97\u6BD4\u8F83\u6D45\uFF0C\u53EA\u8981\u6362\u4EBA\u4E0D\u8981\u592A\u9891\u7E41\uFF08\u592A\u9891\u7E41\u4E86\u8FD8\u662F\u4F1A\u88AB\u9A82\u7684\uFF09\uFF0C\u4E5F\u662F\u9ED8\u8BA4\u6CA1\u95EE\u9898\u7684\u3002</p><p><strong>\u4E2D\u56FD\u793E\u4F1A\u5C31\u662F\u975E\u5E38\u5F3A\u8C03\u975E\u5A5A\u59FB\u6027\u884C\u4E3A\u4E00\u5B9A\u8981\u6302\u4E0A\u201D\u604B\u7231\u201C\u4E24\u4E2A\u5B57\u3002</strong></p><hr><p>\u4E8E\u662F\uFF0C\u4E00\u4E2A\u4E3B\u6D41\u7684\u3001\u5178\u578B\u7684\u3001\u4E0D\u5BCC\u88D5\u7684\u4E2D\u56FD\u7537\u4EBA\u5E94\u8BE5\u662F\u8FD9\u6837\u8FC7\u624D\u80FD\u5B9E\u73B0\uFF08\u5229\u76CA+\u540D\u58F0\u7684\u603B\u548C\uFF09\u6700\u5927\u5316\uFF1A</p><p>1\u3001\u5B66\u751F\u65F6\u4EE3\u604B\u7231\uFF0C\u6DF1\u7231\u6700\u597D\uFF0C\u4E0D\u6DF1\u7231\u4E5F\u884C\u3002</p><p>2\u3001\u4E0E\u604B\u4EBA\u8FC7\u6027\u751F\u6D3B\u3001\u6027\u751F\u6D3B\u5E38\u6001\u5316\u3002\uFF08\u53EF\u4EE5\u6362\u4EBA\uFF0C\u4F46\u4E0D\u53EF\u4EE5\u9891\u7E41\u6362\u4EBA\uFF09</p><p>3\u3001\u5BA2\u89C2\u5F62\u52BF\u4E0D\u5BF9\u5934\uFF08\u8B6C\u5982\u8BF4\u6BD5\u4E1A\u5B63\u5DE5\u4F5C\u5F02\u5730\u7684\u95EE\u9898\uFF09\uFF0C\u60F3\u5206\u5C31\u5206\uFF0C\u4E0D\u8981\u6709\u6127\u759A\u3002</p><p>4\u3001\u65E2\u7136\u4E4B\u524D\u8C08\u8FC7\u82E5\u5E72\u6B21\u604B\u7231\uFF0C\u4E5F\u5F88\u6E05\u695A\u4EC0\u4E48\u5F02\u6027\u9002\u5408\u81EA\u5DF1\u3002\u5230\u4E86\u5DE5\u4F5C\u5730\u4E4B\u540E\u518D\u627E\u7ED3\u5A5A\u5BF9\u8C61\uFF0C\u9002\u5F53\u8003\u8651\u5973\u65B9\u7684\u7ECF\u6D4E\u5B9E\u529B\u3002</p><p>5\u3001\u7ED3\u5A5A\u540E\u5C31\u4E0D\u8981\u6709\u975E\u5A5A\u59FB\u6027\u884C\u4E3A\u4E86\uFF08\u5982\u679C\u6709\u7684\u8BDD\uFF0C\u8BB0\u5F97\u4E00\u5B9A\u8981\u9690\u533F\u597D\uFF09</p><p>\u6240\u8C13\u7684\u201C\u9ED1\u5316\u201D\uFF0C\u5E76\u4E0D\u662F\u8981\u5927\u5BB6\u4E27\u5C3D\u5929\u826F\uFF0C\u800C\u662F\u8BA9\u5927\u5BB6\u5728\u5A5A\u524D\u4EE5\u604B\u7231\u4E3A\u65D7\u53F7\u89E3\u51B3\u6027\u95EE\u9898\uFF0C\u800C\u4E14\u6362\u4EBA\u4E0D\u8981\u9891\u7E41\uFF0C\u4E5F\u5C31\u662F\u6027\u9053\u5FB7\u5E73\u5747\u5316\u3002</p><p><strong>\u522B\u4EBA\u5E72\u4E86\u6CA1\u6709\u53D7\u8C34\u8D23\uFF0C\u4F60\u5E94\u8BE5\u5E72\uFF1B\u522B\u4EBA\u5E72\u4E86\u88AB\u793E\u4F1A\u5927\u529B\u8C34\u8D23\uFF0C\u4F60\u5C31\u4E0D\u5E72\u3002</strong></p><p>\u4E2D\u56FD\u793E\u4F1A\u7684\u8206\u8BBA\u5BF9\u4E8E\u975E\u5A5A\u59FB\u6027\u884C\u4E3A\u53EA\u7ED9\u4E86\u4E00\u4E2A\u51FA\u53E3\uFF1A<strong>\u4E0D\u5904\u4E8E\u5A5A\u59FB\u72B6\u6001\u7684\u4EBA\u7684\u201C\u604B\u7231\u201D\uFF0C\u5373\u4FBF\u8FD9\u4E2A\u6240\u8C13\u7684\u201C\u604B\u7231\u201D\u4E0D\u4E00\u5B9A\u4E25\u8083\u8BA4\u771F\u3002</strong></p><p><strong>\u8BF4\u5F97\u66F4\u76F4\u767D\uFF0C\u793E\u4F1A\u8206\u8BBA\u7ED9\u4E86\u4E00\u4E2A\u4E2D\u56FD\u4EBA\u4E00\u751F\u4E4B\u4E2D\u552F\u4E00\u7684\u4E00\u6B21\u201C\u6D6A\u201D\u7684\u673A\u4F1A\uFF0C\u4F60\u7528\u4E0D\u7528\uFF1F</strong></p><p><strong>\u4F60\u4E0D\u7528\uFF0C\u522B\u4EBA\uFF08\u542B\u4F60\u672A\u6765\u7684\u8001\u5A46\uFF09\u5728\u7528\u3002\u4F60\u4E0D\u7528\uFF0C\u7B49\u4F60\u672A\u6765\u9192\u609F\u4E86\uFF0C\u518D\u5229\u7528\u522B\u7684\u6E20\u9053\u201C\u6D6A\u201D\uFF0C\u793E\u4F1A\u8206\u8BBA\u5C31\u4F1A\u8C34\u8D23\u4F60\u3002</strong></p><hr><p>\u5176\u5B9E\u6211\u7684\u521D\u5FC3\u662F\u529D\u5927\u5BB6\u4FDD\u5B88\u6765\u7740\uFF0C\u4F46\u662F\u6211\u8FD9\u79CD\u903B\u8F91\u5F3A\u5927+\u6D1E\u6089\u793E\u4F1A\u7684\u4EBA\u6DF1\u77E5\u529D\u4FDD\u5B88\u65E0\u6548\u3002\u7B49\u4E0B\u53BB\uFF0C\u53EA\u80FD\u8BA9\u4FDD\u5B88\u7537\u88AB\u793E\u4F1A\u78BE\u538B\u5F97\u6E23\u6E23\u90FD\u4E0D\u5269\uFF0C\u4E8E\u662F\u6211\u4E0D\u5F97\u5DF2\u6539\u529D\u4FDD\u5B88\u7537\u9ED1\u5316\u3002</p><p>\u90FD\u662F\u793E\u4F1A\u5927\u52BF\u903C\u7684\uFF01</p><p>\u8BFB\u5B8C\u672C\u6587\uFF0C\u8BF8\u4F4D\u8BB0\u5F97\u5728\u4EE5\u540E\u7684\u65E5\u5B50\u91CC\u5E2E\u52A9\u6211\u5230\u5904\u63A8\u8350\u534E\u4E1C\u5E08\u5927\u7248\u7684\u300A\u8FDB\u5316\u5FC3\u7406\u5B66\u300B\u3002</p><p>\u6700\u540E\u4E00\u53E5\u8BDD\uFF0C\u4E0D\u80AF\u9ED1\u5316\u7684\u7537\u58EB\u8BF7\u4FE1\u6559\u3002</p>',41),h={href:"https://zhuanlan.zhihu.com/p/104765651",target:"_blank",rel:"noopener noreferrer"},l=_("\u539F\u6587\u94FE\u63A5");function d(i,f){const r=n("ExternalLinkIcon");return o(),e("div",null,[g,p("p",null,[p("a",h,[l,s(r)])])])}var u=t(c,[["render",d],["__file","\u5F53\u4ECA\u4E2D\u56FD\u4EBA\u6027\u884C\u4E3A\u7684\u6F5C\u89C4\u5219.html.vue"]]);export{u as default};
